INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (515, 'Regolamento (UE) 2024/1991 del Parlamento europeo e del Consiglio, del 24 giugno 2024, sul ripristino della natura e che modifica il regolamento (UE) 2022/869', 'Regolamento (UE)', '2024/1991', 'Il Regolamento 2024/1991 stabilisce norme per il ripristino degli ecosistemi degradati al fine di recuperare la biodiversità e rafforzare la resilienza degli ecosistemi terrestri e marini negli Stati membri. Mira a contribuire alla mitigazione e all''adattamento ai cambiamenti climatici, a garantire la sicurezza alimentare e a rispettare gli impegni internazionali dell''Unione. Il regolamento prevede che gli Stati membri attuino misure efficaci per ripristinare almeno il 20% delle zone terrestri e marine entro il 2030 e tutti gli ecosistemi necessari entro il 2050.', 'I regolamenti sono atti giuridici definiti nell’articolo 288 del trattato sul funzionamento dell’Unione europea (TFUE). Hanno portata generale, sono vincolanti in tutti i loro elementi e direttamente applicabili negli Stati membri dell’Unione. I regolamenti rientrano nel diritto derivato dell’Unione. Sono adottati dalle istituzioni dell’Unione europea sulla base dei trattati. Un regolamento ha carattere vincolante in tutti i suoi elementi, ha portata generale ed è rivolto a categorie astratte di persone, non a specifici individui. 
 https://eur-lex.europa.eu/IT/legal-content/summary/european-union-regulations.html', 'Regolamento sul ripristino della natura e che modifica il regolamento (UE) 2022/869', 'Regolamento (UE) - 2024/1991', 'Regolamento (UE) 2024/1991', NULL);

