INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (517, 'SDGs: Goal 15 La vita sulla terra', 'SDGs', 'Goal 15', 'Proteggere, ripristinare e favorire un uso sostenibile dell’ecosistema terrestre', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'La vita sulla terra', 'SDGs - Goal 15', 'SDG 15.5', 'Intraprendere azioni efficaci ed immediate per ridurre il degrado degli ambienti naturali, arrestare la distruzione della biodiversità e, entro il 2020, proteggere le specie a rischio di estinzione');

-- Record 1038/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (517, 'Framework globale sulla biodiversità Kunming-Montreal (GBF)- Target 15', 'Framework globale', 'Biodiversità (Kunming-Montreal) - Target 15', 'Adottare misure legali, amministrative o politiche per incoraggiare e abilitare le imprese, e in particolare garantire che le grandi aziende transnazionali e le istituzioni finanziarie: (a) monitorino regolarmente, valutino e divulghino in modo trasparente i loro rischi, le loro dipendenze e gli impatti sulla biodiversità, compresi i requisiti per tutte le grandi aziende e istituzioni finanziarie transnazionali lungo le loro operazioni, catene di fornitura e di valore, e portafogli; (b) forniscano informazioni necessarie ai consumatori per promuovere modelli di consumo sostenibili; (c) Riportino la conformità alle normative e misure di accesso e ripartizione dei benefici, ove applicabile; al fine di ridurre progressivamente gli impatti negativi sulla biodiversità, aumentare gli impatti positivi, ridurre i rischi legati alla biodiversità per le imprese e le istituzioni finanziarie e promuovere azioni per garantire modelli di produzione sostenibili.', 'Un "framework globale" si riferisce a un insieme di principi, linee guida, regole o standard internazionalmente riconosciuti e accettati che mirano a indirizzare e coordinare le azioni dei diversi paesi, organizzazioni internazionali, aziende e altre parti interessate su una determinata questione.', 'Target 15 (2030): Le imprese valutano, divulghino e riducono i rischi e gli impatti negativi legati alla biodiversità.', 'Framework globale - Biodiversità (Kunming-Montreal) - Target 15', 'GBF- Target 15 (2030)', 'Le imprese valutano, divulghino e riducono i rischi e gli impatti negativi legati alla biodiversità.');

-- Record 1039/2644
