INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (517, 'GRI 304- Biodiversità 2016', 'GRI', '304', 'Questo standard contiene le informative per le organizzazioni che devono rendicontare informazioni sui loro impatti correlati alla biodiversità e su come li gestiscono. Dal 1º gennaio 2026, questo standard verrà sostituito dal GRI 101 Biodiversità 2024.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Biodiversità (2016)', 'GRI - 304 (2016)', 'GRI 304-2 (2016)', 'Impatti significativi di attività, prodotti e servizi sulla biodiversità');

