INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (517, 'Decreto Legislativo 34/2018 - Testo Unico in materia di foreste e filiere forestali', 'Decreto (IT)', '34/2018', 'Il Decreto Legislativo 34/2018, noto anche come Testo Unico in materia di foreste e filiere forestali, stabilisce norme e disposizioni relative alla gestione delle foreste, alla conservazione della biodiversità forestale e alla promozione delle filiere legate al settore forestale in Italia.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Testo Unico in materia di foreste e filiere forestali', 'Decreto Legislativo - 34/2018', 'D.Lgs. 34/2018', NULL);

-- Record 1049/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (517, 'Direttiva 92/43/CEE del Consiglio, del 21 maggio 1992, relativa alla conservazione degli habitat naturali e seminaturali e della flora e della fauna selvatiche', 'Direttiva (UE)', '92/43/CEE', 'Lo scopo della direttiva è contribuire a salvaguardare la biodiversità mediante la conservazione degli habitat naturali, nonché della flora e della fauna selvatiche nel territorio europeo degli Stati membri al quale si applica il trattato', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva relativa alla conservazione degli habitat naturali e seminaturali e della flora e della fauna selvatiche', 'Direttiva (UE) - 92/43/CEE', 'Direttiva (UE) 92/43/CEE', NULL);

-- Record 1050/2644
