INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (518, 'GRI 304- Biodiversità 2016', 'GRI', '304', 'Questo standard contiene le informative per le organizzazioni che devono rendicontare informazioni sui loro impatti correlati alla biodiversità e su come li gestiscono. Dal 1º gennaio 2026, questo standard verrà sostituito dal GRI 101 Biodiversità 2024.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Biodiversità (2016)', 'GRI - 304 (2016)', 'GRI 304-2 (2016)', 'Impatti significativi di attività, prodotti e servizi sulla biodiversità');

-- Record 1059/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (518, 'ESRS E4- Biodiversità ed ecosistemi', 'ESRS', 'E4', 'Questo Standard stabilisce i requisiti di Informativa relativi alla relazione dell''impresa con gli habitat terrestri, d''acqua dolce e marini, gli ecosistemi e le popolazioni di specie faunistiche e floristiche correlate, inclusa la diversità all''interno delle specie, tra le specie e degli ecosistemi, e la loro interrelazione con i popoli indigeni e altre comunità colpite.
 
 I termini "biodiversità" e "diversità biologica" si riferiscono alla variabilità tra gli organismi viventi di tutte le provenienze, compresi, tra l''altro, gli ecosistemi terrestri, d''acqua dolce, marini e altri ecosistemi acquatici e i complessi ecologici di cui fanno parte.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Biodiversità ed ecosistemi', 'ESRS - E4', 'ESRS (E4) IRO-1 17 c; AR 9', 'Dichiarazione relativa alla presenza e modalità di identificazione e valutazione dei rischi e delle opportunità di transizione e fisici legati alla biodiversità e agli ecosistemi.');

-- Record 1060/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (518, 'Global Compact delle Nazioni Unite (UNGC): Principio 7- Ambiente', 'UNGC', 'Principio 7', 'Le imprese dovrebbero sostenere un approccio precauzionale alle sfide ambientali', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Ambiente', 'UNGC - Principio 7', 'UNGC P.7', 'Le imprese dovrebbero sostenere un approccio precauzionale alle sfide ambientali');

-- Record 1061/2644
INSERT INTO esg_impact.form_legal_bases
