INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (518, 'Piano Nazionale per la realizzazione di aree verdi permanenti', 'Piano Nazionale (IT)', 'Realizzazione di aree verdi permanenti', 'Il Piano Nazionale per la Realizzazione di Aree Verdi Permanenti è un''iniziativa volta a promuovere la creazione e la manutenzione di spazi verdi duraturi in Italia. Gli obiettivi principali di questo piano includono:
 - Miglioramento della qualità dell''aria; - Promozione della biodiversità; - Benessere delle comunità; - Mitigazione dei cambiamenti climatici; - Valorizzazione del territorio', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Piano per la realizzazione di aree verdi permanenti', 'Piano Nazionale (IT) - Realizzazione di aree verdi permanenti', 'PN-realizzazione di aree verdi permanenti', NULL);

