INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (519, 'Direttiva (UE) 2022/2464 del Parlamento europeo e del Consiglio del 14 dicembre 2022 che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE)', '2022/2464', 'La CSRD o Corporate Sustainability Reporting Directive (Direttiva 2022/2464) è la direttiva europea che mira a migliorare la trasparenza e la qualità delle informazioni sulla sostenibilità fornite dalle aziende. La CSRD sostituisce e amplia la Non-Financial Reporting Directive (NFRD), aumentando le esigenze di rendicontazione per le imprese riguardo alle loro attività sostenibili. I punti chiavi della CSRD sono: -L''ampiezza del campo di applicazione rispetto alla NFRD rispetto alle aziende che devono rendicontare, - le informazioni da rendicontare in termini ESG e di sostenibilità; - lo standard di rendicontazione (gli ESRS); - L''audit e certificazione; e la digitalizzazione delle informazioni. 
 
 L''obiettivo principale della CSRD è migliorare la trasparenza e responsabilizzare le aziende verso pratiche più sostenibili, aiutando investitori, consumatori, e altre parti interessate a prendere decisioni informate basate su dati di sostenibilità accurati e completi.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE) - 2022/2464', 'CSRD', 'Rendicontazione societaria di sostenibilità (CSRD)');

-- Record 1094/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (519, 'Decreto 18 settembre 2001, n. 468: Regolamento recante: "Programma nazionale di bonifica e ripristino ambientale"', 'Decreto (IT)', '468', 'Il Decreto del Ministero dell''Ambiente e della Tutela del Territorio 468 ha adottato il programma nazionale di bonifica e ripristino ambientale dei siti inquinati di interesse nazionale, con i relativi allegati che costituiscono parte integrante del presente decreto.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Regolamento recante: "Programma nazionale di bonifica e ripristino ambientale"', 'Decreto - 468', 'D.p.r 468/2001', NULL);

-- Record 1095/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (519, 'Piano Nazionale per la realizzazione di aree verdi permanenti', 'Piano Nazionale (IT)', 'Realizzazione di aree verdi permanenti', 'Il Piano Nazionale per la Realizzazione di Aree Verdi Permanenti è un''iniziativa volta a promuovere la creazione e la manutenzione di spazi verdi duraturi in Italia. Gli obiettivi principali di questo piano includono:
 - Miglioramento della qualità dell''aria; - Promozione della biodiversità; - Benessere delle comunità; - Mitigazione dei cambiamenti climatici; - Valorizzazione del territorio', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Piano per la realizzazione di aree verdi permanenti', 'Piano Nazionale (IT) - Realizzazione di aree verdi permanenti', 'PN-realizzazione di aree verdi permanenti', NULL);

-- Record 1096/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (519, 'Piano Nazionale Adattamento Cambiamenti Climatici (PNACC)', 'Piano Nazionale (IT)', 'Adattamento Cambiamento Climatico', 'La finalità del Piano è contenere la vulnerabilità dei sistemi naturali, sociali ed economici agli impatti dei cambiamenti climatici e aumentarne la resilienza. Rappresenta lo strumento di attuazione della Strategia Nazionale di adattamento ai cambiamenti climatici del 2015 per la pianificazione nazionale a supporto delle istituzioni che saranno chiamate a sviluppare sulla propria scala di governo i contenuti del piano, tenendo conto delle specificità dei diversi contesti.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Cambiamento Climatico (PNACC)', 'Piano Nazionale (IT) Adattamento Cambiamento Climatico', 'PNACC', 'Piano Nazionale Adattamento Cambiamenti Climatici');

-- Record 1097/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (519, 'Regolamento (UE) 2024/1991 del Parlamento europeo e del Consiglio, del 24 giugno 2024, sul ripristino della natura e che modifica il regolamento (UE) 2022/869', 'Regolamento (UE)', '2024/1991', 'Il Regolamento 2024/1991 stabilisce norme per il ripristino degli ecosistemi degradati al fine di recuperare la biodiversità e rafforzare la resilienza degli ecosistemi terrestri e marini negli Stati membri. Mira a contribuire alla mitigazione e all''adattamento ai cambiamenti climatici, a garantire la sicurezza alimentare e a rispettare gli impegni internazionali dell''Unione. Il regolamento prevede che gli Stati membri attuino misure efficaci per ripristinare almeno il 20% delle zone terrestri e marine entro il 2030 e tutti gli ecosistemi necessari entro il 2050.', 'I regolamenti sono atti giuridici definiti nell’articolo 288 del trattato sul funzionamento dell’Unione europea (TFUE). Hanno portata generale, sono vincolanti in tutti i loro elementi e direttamente applicabili negli Stati membri dell’Unione. I regolamenti rientrano nel diritto derivato dell’Unione. Sono adottati dalle istituzioni dell’Unione europea sulla base dei trattati. Un regolamento ha carattere vincolante in tutti i suoi elementi, ha portata generale ed è rivolto a categorie astratte di persone, non a specifici individui. 
 https://eur-lex.europa.eu/IT/legal-content/summary/european-union-regulations.html', 'Regolamento sul ripristino della natura e che modifica il regolamento (UE) 2022/869', 'Regolamento (UE) - 2024/1991', 'Regolamento (UE) 2024/1991', NULL);

-- Record 1098/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (519, 'Decreto Legislativo 6 settembre 2024, n. 125 Attuazione della direttiva 2022/2464/UE del Parlamento europeo e del Consiglio del 14 dicembre 2022, recante modifica del regolamento 537/2014/UE, della direttiva 2004/109/CE, della direttiva 2006/43/CE e della direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Decreto (IT)', '125/2024', 'Il Decreto legislativo 6 settembre 2024, n. 125 attua la Direttiva 2022/2464/UE (nota come Corporate Sustainability Reporting Directive, CSRD). Questo decreto introduce nuovi obblighi di rendicontazione societaria di sostenibilità per le imprese, ampliando notevolmente le informazioni che devono essere incluse nei bilanci aziendali in materia di sostenibilità ambientale, sociale e di governance (ESG).', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Attuazione della direttiva 2022/2464/UE (CSRD)', 'Decreto Legislativo - 125/2024', 'D.Lgs. 125/2024', 'Attuazione della direttiva 2022/2464/UE (CSRD)');

-- Record 1099/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (520, 'SDGs: Goal 15 La vita sulla terra', 'SDGs', 'Goal 15', 'Proteggere, ripristinare e favorire un uso sostenibile dell’ecosistema terrestre', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'La vita sulla terra', 'SDGs - Goal 15', 'SDG 15.5', 'Intraprendere azioni efficaci ed immediate per ridurre il degrado degli ambienti naturali, arrestare la distruzione della biodiversità e, entro il 2020, proteggere le specie a rischio di estinzione');

-- Record 1100/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (520, 'Framework globale sulla biodiversità Kunming-Montreal (GBF)- Target 15', 'Framework globale', 'Biodiversità (Kunming-Montreal) - Target 15', 'Adottare misure legali, amministrative o politiche per incoraggiare e abilitare le imprese, e in particolare garantire che le grandi aziende transnazionali e le istituzioni finanziarie: (a) monitorino regolarmente, valutino e divulghino in modo trasparente i loro rischi, le loro dipendenze e gli impatti sulla biodiversità, compresi i requisiti per tutte le grandi aziende e istituzioni finanziarie transnazionali lungo le loro operazioni, catene di fornitura e di valore, e portafogli; (b) forniscano informazioni necessarie ai consumatori per promuovere modelli di consumo sostenibili; (c) Riportino la conformità alle normative e misure di accesso e ripartizione dei benefici, ove applicabile; al fine di ridurre progressivamente gli impatti negativi sulla biodiversità, aumentare gli impatti positivi, ridurre i rischi legati alla biodiversità per le imprese e le istituzioni finanziarie e promuovere azioni per garantire modelli di produzione sostenibili.', 'Un "framework globale" si riferisce a un insieme di principi, linee guida, regole o standard internazionalmente riconosciuti e accettati che mirano a indirizzare e coordinare le azioni dei diversi paesi, organizzazioni internazionali, aziende e altre parti interessate su una determinata questione.', 'Target 15 (2030): Le imprese valutano, divulghino e riducono i rischi e gli impatti negativi legati alla biodiversità.', 'Framework globale - Biodiversità (Kunming-Montreal) - Target 15', 'GBF- Target 15 (2030)', 'Le imprese valutano, divulghino e riducono i rischi e gli impatti negativi legati alla biodiversità.');

-- Record 1101/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (520, 'GRI 304- Biodiversità 2016', 'GRI', '304', 'Questo standard contiene le informative per le organizzazioni che devono rendicontare informazioni sui loro impatti correlati alla biodiversità e su come li gestiscono. Dal 1º gennaio 2026, questo standard verrà sostituito dal GRI 101 Biodiversità 2024.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Biodiversità (2016)', 'GRI - 304 (2016)', 'GRI 304-1 (2016)', 'Siti operativi di proprietà, concessi in leasing o gestiti in aree protette e in aree di elevato valore in termini di biodiversità fuori da aree protette oppure vicini a tali aree');

