INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (520, 'Strategia Nazionale per la Biodiversità al 2030', 'Strategia Nazionale (IT)', 'Biodiversità (2030)', 'La Strategia Nazionale per la Biodiversità al 2030 in Italia è un piano d''azione elaborato per proteggere, conservare e ripristinare la biodiversità del paese entro l''anno 2030. Prevede l’identificazione di due obiettivi strategici declinati in otto Ambiti di intervento (Aree Protette; Specie, Habitat ed Ecosistemi; Cibo e Sistemi Agricoli, Zootecnia; Foreste; Verde Urbano; Acque Interne; Mare; Suolo)', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia Nazionale per la Biodiversità al 2030', 'Strategia Nazionale - Biodiversità (2030)', 'Strategia (IT)  Biodiversità 2030', 'Strategia Nazionale per la Biodiversità al 2030');

-- Record 1107/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (520, 'Green Deal Europeo (EGD)', 'Strategia (UE)', 'Green Deal Europeo', 'Il Green Deal europeo è un pacchetto di iniziative strategiche che mira ad avviare l''UE sulla strada di una transizione verde, con l''obiettivo ultimo di raggiungere la neutralità climatica entro il 2050.', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Strategia dell''UE per conseguire l''obiettivo della neutralità climatica entro il 2050', 'Strategia (UE) - Green Deal Europeo', 'Green Deal Europeo', 'Green Deal Europeo: Strategia dell''UE per conseguire l''obiettivo della neutralità climatica entro il 2050');

