INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (520, 'Direttiva 92/43/CEE del Consiglio, del 21 maggio 1992, relativa alla conservazione degli habitat naturali e seminaturali e della flora e della fauna selvatiche', 'Direttiva (UE)', '92/43/CEE', 'Lo scopo della direttiva è contribuire a salvaguardare la biodiversità mediante la conservazione degli habitat naturali, nonché della flora e della fauna selvatiche nel territorio europeo degli Stati membri al quale si applica il trattato', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva relativa alla conservazione degli habitat naturali e seminaturali e della flora e della fauna selvatiche', 'Direttiva (UE) - 92/43/CEE', 'Direttiva (UE) 92/43/CEE', NULL);

