INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (523, 'Costituzione Italiana- Articolo 41: Libertà di iniziativa economica', 'Costituzione Italiana', 'Articolo 41', 'L''articolo 41 della Costituzione italiana disciplina la libertà di iniziativa economica privata e stabilisce i limiti entro i quali tale libertà può essere esercitata.', 'La Costituzione Italiana è la legge fondamentale della Repubblica Italiana. Essa stabilisce i principi fondamentali su cui si basa l''ordinamento dello Stato italiano, definendo i diritti e i doveri dei cittadini, l''organizzazione e i poteri delle istituzioni statali (Parlamento, Governo e Magistratura), nonché i principi economici e sociali dello Stato. La Costituzione è la norma suprema, alla quale devono conformarsi tutte le altre leggi e normative del Paese.', 'Libertà di iniziativa economica', 'Costituzione Italiana - Articolo 41', 'Art. 41 Cost.', 'Libertà di iniziativa economica privata');

-- Record 1153/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (524, 'SDGs: Goal 16 Pace, giustizia, e istituzioni solide', 'SDGs', 'Goal 16', 'Promuovere società pacifiche e inclusive per lo sviluppo sostenibile, garantire accesso alla giustizia per tutti e costruire istituzioni efficaci, responsabili e inclusive a tutti i livelli.', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Pace, giustizia, e istituzioni solide', 'SDGs - Goal 16', 'SDG 16.5', 'Ridurre sensibilmente la corruzione e gli abusi di potere in tutte le loro forme');

-- Record 1154/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (524, 'GRI 205- Anticorruzione (2016)', 'GRI', '205', 'Questo Standard tratta il tema dell’anticorruzione. In questo Standard il termine “corruzione” indica varie prassi illecite quali pagamenti facilitativi, frode, estorsione, collusione e riciclaggio di denaro; l’offerta o il ricevimento di omaggi, prestiti, commissioni, premi o altri vantaggi come incentivo a compiere un atto disonesto, illegale o che rappresenta una violazione della fiducia. Può indicare anche prassi quali appropriazione indebita, commercio dell’autorità di cui si gode, abuso della funzione svolta, arricchimento illecito, occultamento e ostruzione della giustizia.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Anticorruzione (2016)', 'GRI - 205 (2016)', 'GRI 205-2 (2016)', 'Comunicazione e formazione su normative e procedure anticorruzione');

-- Record 1155/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (524, 'ESRS G1- Condotta aziendale', 'ESRS', 'G1', 'L''obiettivo di questo Standard è specificare i requisiti di divulgazione che consentiranno agli utenti delle dichiarazioni di sostenibilità dell''azienda di comprendere la strategia e l''approccio dell''azienda, i processi e le procedure, nonché le sue performance riguardo alla condotta aziendale.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Condotta aziendale', 'ESRS - G1', 'ESRS (G1) G1-3 18a AR 5-AR 6', 'Informazioni sulle procedure in atto per prevenire, rilevare e affrontare accuse o incidenti di corruzione o concussione');

-- Record 1156/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (524, 'Global Compact delle Nazioni Unite (UNGC): Principio 10- anticorruzione', 'UNGC', 'Principio 10', 'Le imprese dovrebbero impegnarsi contro la corruzione in tutte le sue forme, comprese l''estorsione e la concussione.', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Anticorruzione', 'UNGC - Principio 10', 'UNGC P.10', 'Le imprese dovrebbero impegnarsi contro la corruzione in tutte le sue forme, comprese l''estorsione e la concussione.');

-- Record 1157/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (524, 'Linee guida dell''OCSE per le imprese multinazionali sulla condotta responsabile delle imprese: Principio 5', 'OECD', 'Linee guida - Principio 5', 'Il principio 5 stabilisce che le imprese dovrebbero: Le imprese dovrebbero astenersi dal ricercare o dall’accettare esenzioni non contemplate nelle norme di legge o regolamentari relative ai diritti umani, all’ambiente, alla salute, alla sicurezza, al lavoro, alla fiscalità, agli incentivi finanziari o ad altre materie.', 'Le Linee guida dell''OCSE per le imprese multinazionali sono raccomandazioni governative rivolte alle aziende, volte a promuovere una condotta responsabile. L''obiettivo è incentivare contributi positivi al progresso economico, ambientale e sociale, riducendo al contempo gli impatti negativi legati alle operazioni, ai prodotti e ai servizi aziendali. Le Linee guida coprono tutte le principali aree di responsabilità d''impresa, tra cui diritti umani, diritti dei lavoratori, ambiente, corruzione, interessi dei consumatori, trasparenza, scienza e tecnologia, concorrenza e fiscalità.', 'Linee guida per le imprese multinazionali sulla condotta responsabile delle imprese', 'OECD - Linee guida - Principio 5', 'Linee guida OECD P.5', 'Le imprese dovrebbero astenersi dal ricercare o dall’accettare esenzioni non contemplate nelle norme di legge o regolamentari relative ai diritti umani, all’ambiente, alla salute, alla sicurezza, al lavoro, alla fiscalità, agli incentivi finanziari o ad altre materie.');

-- Record 1158/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (524, 'Decreto Legislativo 231/2001 - Modello di organizzazione e gestione', 'Decreto (IT)', '231/2001', 'Il Decreto Legislativo 231/2001, noto anche come "Modello di organizzazione, gestione e controllo" delle società, enti e associazioni, è una normativa italiana che introduce responsabilità amministrative e sanzioni per le persone giuridiche (società, enti, associazioni) in caso di reati commessi nell''interesse o a vantaggio di tali entità.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Modello di organizzazione e gestione', 'Decreto Legislativo - 231/2001', 'D.Lgs. 231/2001', NULL);

-- Record 1159/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (524, 'Direttiva (UE) 2022/2464 del Parlamento europeo e del Consiglio del 14 dicembre 2022 che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE)', '2022/2464', 'La CSRD o Corporate Sustainability Reporting Directive (Direttiva 2022/2464) è la direttiva europea che mira a migliorare la trasparenza e la qualità delle informazioni sulla sostenibilità fornite dalle aziende. La CSRD sostituisce e amplia la Non-Financial Reporting Directive (NFRD), aumentando le esigenze di rendicontazione per le imprese riguardo alle loro attività sostenibili. I punti chiavi della CSRD sono: -L''ampiezza del campo di applicazione rispetto alla NFRD rispetto alle aziende che devono rendicontare, - le informazioni da rendicontare in termini ESG e di sostenibilità; - lo standard di rendicontazione (gli ESRS); - L''audit e certificazione; e la digitalizzazione delle informazioni. 
 
 L''obiettivo principale della CSRD è migliorare la trasparenza e responsabilizzare le aziende verso pratiche più sostenibili, aiutando investitori, consumatori, e altre parti interessate a prendere decisioni informate basate su dati di sostenibilità accurati e completi.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE) - 2022/2464', 'CSRD', 'Rendicontazione societaria di sostenibilità (CSRD)');

-- Record 1160/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (524, 'ISO 37001: Sistemi di gestione per la prevenzione della corruzione', 'ISO', '37001', 'L''ISO 37001 è uno standard internazionale che fornisce linee guida e requisiti per l''implementazione di un sistema di gestione antisovvenzione (Anti-Bribery Management System, ABMS). L''obiettivo principale è quello di aiutare le organizzazioni a stabilire, implementare, mantenere, aggiornare e migliorare un sistema di gestione efficace per prevenire la corruzione e aderire ai requisiti legali e regolamentari applicabili.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Sistemi di gestione per la prevenzione della corruzione', 'ISO - 37001', 'ISO 37001', 'Sistemi di gestione per la prevenzione della corruzione');

-- Record 1161/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (524, 'Decreto Legislativo 6 settembre 2011, n. 159 (Codice delle leggi antimafia e delle misure di prevenzione, nonche'' nuove disposizioni in materia di documentazione antimafia, a norma degli articoli 1 e 2 della legge 13 agosto 2010, n. 136)', 'Decreto (IT)', '159', 'Il D. Lgs. 159 stabilisce il Codice delle leggi antimafia e delle misure di prevenzione. Il decreto mira a migliorare l''efficacia delle indagini e delle azioni preventive contro la criminalità organizzata, contribuendo alla sicurezza e al rispetto delle norme nel contesto italiano.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Codice delle leggi antimafia e delle misure di prevenzione, nonche'' nuove disposizioni in materia di documentazione antimafia', 'Decreto Legislativo - 159', 'D.Lgs. 159/2011', NULL);

