INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (525, 'Linee guida dell''OCSE per le imprese multinazionali sulla condotta responsabile delle imprese: Principio 8', 'OECD', 'Linee guida - Principio 8', 'Il principio 8 stabilisce che le imprese dovrebbero: Promuovere fra i lavoratori alle dipendenze delle imprese multinazionali la consapevolezza delle politiche aziendali e l’adesione alle stesse, attraverso una loro appropriata divulgazione, anche tramite programmi di formazione.', 'Le Linee guida dell''OCSE per le imprese multinazionali sono raccomandazioni governative rivolte alle aziende, volte a promuovere una condotta responsabile. L''obiettivo è incentivare contributi positivi al progresso economico, ambientale e sociale, riducendo al contempo gli impatti negativi legati alle operazioni, ai prodotti e ai servizi aziendali. Le Linee guida coprono tutte le principali aree di responsabilità d''impresa, tra cui diritti umani, diritti dei lavoratori, ambiente, corruzione, interessi dei consumatori, trasparenza, scienza e tecnologia, concorrenza e fiscalità.', 'Linee guida per le imprese multinazionali sulla condotta responsabile delle imprese', 'OECD - Linee guida - Principio 8', 'Linee guida OECD P.8', 'Promuovere fra i lavoratori alle dipendenze delle imprese multinazionali la consapevolezza delle politiche aziendali e l’adesione alle stesse, attraverso una loro appropriata divulgazione, anche tramite programmi di formazione.');

-- Record 1175/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (525, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 16: La dichiarazione di intenti sull’impegno dell’impresa', 'UNGPs', 'Principio 16', 'Come base per interiorizzare la propria responsabilità di rispettare i diritti umani, le imprese dovrebbero esprimere il proprio impegno ad adempiere tale responsabilità attraverso una dichiarazione di intenti che:
 
 a) sia approvata dai vertici dell’impresa;
 b) sia stabilita attraverso la consulenza di esperti in materia interni e/o esterni; 
 c) definisca ciò che l’impresa si attende da dipendenti, partner commerciali e dalle altre parti direttamente collegate alle proprie attività, ai propri prodotti o ai propri servizi in materia di diritti umani; d) sia di pubblico dominio e divulgata all’interno o all’esterno a tutti i dipendenti, a tutti i partner commerciali e a tutte le altre parti interessate; 
 e) sia rispecchiata nelle politiche e nelle procedure operative necessarie per la sua integrazione in tutta l’impresa.', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'La dichiarazione di intenti sull’impegno dell’impresa', 'UNGPs - Principio 16', 'UNGPs P.16', 'Come base per interiorizzare la propria responsabilità di rispettare i diritti umani, le imprese dovrebbero esprimere il proprio impegno ad adempiere tale responsabilità attraverso una dichiarazione di intenti che:
a) sia approvata dai vertici dell’impresa;
b) sia stabilita attraverso la consulenza di esperti in materia interni e/o esterni; 
c) definisca ciò che l’impresa si attende da dipendenti, partner commerciali e dalle altre parti direttamente collegate alle proprie attività, ai propri prodotti o ai propri servizi in materia di diritti umani; d) sia di pubblico dominio e divulgata all’interno o all’esterno a tutti i dipendenti, a tutti i partner commerciali e a tutte le altre parti interessate; 
e) sia rispecchiata nelle politiche e nelle procedure operative necessarie per la sua integrazione in tutta l’impresa.');

-- Record 1176/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (525, 'Legge 300/1970 - Statuto dei Lavoratori', 'Legge (IT)', '300/1970', 'Legge che stabilisce le norme sulla tutela della liberta'' e dignita'' dei lavoratori, della liberta'' sindacale e dell''attivita'' sindacale, nei luoghi di lavoro e norme sul collocamento.', 'Il termine legge viene usato nel diritto con una molteplicità di significati, si distingue in:
la norma giuridica; l’atto normativo (legge in senso materiale); l’atto giuridico attraverso il quale il potere legislativo esercita la sua funzione (legge in senso formale); l’insieme delle norme giuridiche che costituiscono l’ordinamento giuridico, ossia il diritto oggettivo. Per maggiori informazioni accedere al link:
https://www.governo.it/it/costituzione-italiana/principi-fondamentali/2839', 'Statuto dei Lavoratori', 'Legge (IT) - 300/1970', 'Legge 300/1970', 'Statuto dei Lavoratori');

-- Record 1177/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (525, 'ISO 26000: responsabilità sociale delle organizzazioni', 'ISO', '26000', 'Fornisce linee guida su come le imprese e le organizzazioni possano operare in modo etico e trasparente, contribuendo allo sviluppo sostenibile e tenendo conto delle aspettative degli stakeholder, delle leggi applicabili e delle norme internazionali di comportamento.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Responsabilità sociale delle organizzazioni', 'ISO - 26000', 'ISO 26000', 'Responsabilità sociale delle organizzazioni');

-- Record 1178/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (525, 'ISO 37001: Sistemi di gestione per la prevenzione della corruzione', 'ISO', '37001', 'L''ISO 37001 è uno standard internazionale che fornisce linee guida e requisiti per l''implementazione di un sistema di gestione antisovvenzione (Anti-Bribery Management System, ABMS). L''obiettivo principale è quello di aiutare le organizzazioni a stabilire, implementare, mantenere, aggiornare e migliorare un sistema di gestione efficace per prevenire la corruzione e aderire ai requisiti legali e regolamentari applicabili.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Sistemi di gestione per la prevenzione della corruzione', 'ISO - 37001', 'ISO 37001', 'Sistemi di gestione per la prevenzione della corruzione');

-- Record 1179/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (525, 'Principi di Corporate Governance G20/OECD (2023)- Principio V', 'Principi G20/OECD', 'Principio V', 'Il principio V stabilische che il quadro di governance aziendale dovrebbe garantire l''orientamento strategico dell''azienda, un monitoraggio efficace della gestione da parte del consiglio di amministrazione e la responsabilità del consiglio nei confronti dell''azienda e degli azionisti.', 'I Principi di Corporate Governance G20/OCSE sono lo standard internazionale per il governo societario. I Principi aiutano i responsabili politici a valutare e migliorare il quadro legale, regolamentare e istituzionale per il governo societario, con l''obiettivo di supportare l''efficienza economica, la crescita sostenibile e la stabilità finanziaria. Per maggiori informazioni vedere: https://legalinstruments.oecd.org/public/doc/322/322.en.pdf', 'Le responsabilità del consiglio di amministrazione', 'Principi - Corporate Governance G20/OECD - V', 'Principi G20/OECD: V.C.', 'Il consiglio di amministrazione dovrebbe adottare elevati standard etici.');

-- Record 1180/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (526, 'SDGs: Goal 16 Pace, giustizia, e istituzioni solide', 'SDGs', 'Goal 16', 'Promuovere società pacifiche e inclusive per lo sviluppo sostenibile, garantire accesso alla giustizia per tutti e costruire istituzioni efficaci, responsabili e inclusive a tutti i livelli.', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Pace, giustizia, e istituzioni solide', 'SDGs - Goal 16', 'SDG 16.5', 'Ridurre sensibilmente la corruzione e gli abusi di potere in tutte le loro forme');

-- Record 1181/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (526, 'GRI 2- Informativa generale (2021)', 'GRI', '2', 'GRI che contiene le informative per le organizzazioni che devono fornire informazioni sulle loro prassi di rendicontazione, sulle attività e sui lavoratori, sulla governance, sulla strategia, sulle politiche e sulle  pratiche e sul coinvolgimento degli stakeholder', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Informativa generale (2021)', 'GRI - 2 (2021)', 'GRI 2-24 (2021)', 'Integrazione degli impegni in termini di policy');

-- Record 1182/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (526, 'ESRS G1- Condotta aziendale', 'ESRS', 'G1', 'L''obiettivo di questo Standard è specificare i requisiti di divulgazione che consentiranno agli utenti delle dichiarazioni di sostenibilità dell''azienda di comprendere la strategia e l''approccio dell''azienda, i processi e le procedure, nonché le sue performance riguardo alla condotta aziendale.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Condotta aziendale', 'ESRS - G1', 'ESRS (G1) G1-1 9, AR 1', 'Descrizione di come l''impresa stabilisce, sviluppa, promuove e valuta la propria cultura aziendale.');

-- Record 1183/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (526, 'Global Compact delle Nazioni Unite (UNGC): Principio 10- anticorruzione', 'UNGC', 'Principio 10', 'Le imprese dovrebbero impegnarsi contro la corruzione in tutte le sue forme, comprese l''estorsione e la concussione.', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Anticorruzione', 'UNGC - Principio 10', 'UNGC P.10', 'Le imprese dovrebbero impegnarsi contro la corruzione in tutte le sue forme, comprese l''estorsione e la concussione.');

-- Record 1184/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (526, 'Linee guida dell''OCSE per le imprese multinazionali sulla condotta responsabile delle imprese: Principio 5', 'OECD', 'Linee guida - Principio 5', 'Il principio 5 stabilisce che le imprese dovrebbero: Le imprese dovrebbero astenersi dal ricercare o dall’accettare esenzioni non contemplate nelle norme di legge o regolamentari relative ai diritti umani, all’ambiente, alla salute, alla sicurezza, al lavoro, alla fiscalità, agli incentivi finanziari o ad altre materie.', 'Le Linee guida dell''OCSE per le imprese multinazionali sono raccomandazioni governative rivolte alle aziende, volte a promuovere una condotta responsabile. L''obiettivo è incentivare contributi positivi al progresso economico, ambientale e sociale, riducendo al contempo gli impatti negativi legati alle operazioni, ai prodotti e ai servizi aziendali. Le Linee guida coprono tutte le principali aree di responsabilità d''impresa, tra cui diritti umani, diritti dei lavoratori, ambiente, corruzione, interessi dei consumatori, trasparenza, scienza e tecnologia, concorrenza e fiscalità.', 'Linee guida per le imprese multinazionali sulla condotta responsabile delle imprese', 'OECD - Linee guida - Principio 5', 'Linee guida OECD P.5', 'Le imprese dovrebbero astenersi dal ricercare o dall’accettare esenzioni non contemplate nelle norme di legge o regolamentari relative ai diritti umani, all’ambiente, alla salute, alla sicurezza, al lavoro, alla fiscalità, agli incentivi finanziari o ad altre materie.');

