INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (527, 'ISO 9001: Certificazione del Sistema di Gestione per la Qualità', 'ISO', '9001', 'La norma ISO 9001 specifica i requisiti per l''istituzione, il mantenimento e il miglioramento continuo di un sistema di gestione della qualità, coprendo una vasta gamma di argomenti, tra cui:
 - Contesto dell''organizzazione, - Leadership, - Pianificazione, - Supporto, - Valutazione delle prestazioni, - Miglioramento', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Certificazione del Sistema di Gestione per la Qualità', 'ISO - 9001', 'ISO 9001', 'Certificazione del Sistema di Gestione per la Qualità');

