INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (528, 'ESRS G1- Condotta aziendale', 'ESRS', 'G1', 'L''obiettivo di questo Standard è specificare i requisiti di divulgazione che consentiranno agli utenti delle dichiarazioni di sostenibilità dell''azienda di comprendere la strategia e l''approccio dell''azienda, i processi e le procedure, nonché le sue performance riguardo alla condotta aziendale.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Condotta aziendale', 'ESRS - G1', 'ESRS (G1) GOV-1  5a', 'Dichiarazione del ruolo degli organi amministrativi, di gestione e di supervisione in relazione alla condotta aziendale.');

