INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (532, 'Regolamento (UE) 2021/1119 del Parlamento europeo e del Consiglio del 30 giugno 2021 che istituisce il quadro per il conseguimento della neutralità climatica e che modifica il regolamento (CE) n. 401/2009 e il regolamento (UE) 2018/1999 («European Climate Law»)', 'Regolamento (UE)', '2021/1119', 'Quadro per il conseguimento della neutralità climatica e che modifica il regolamento (CE) n. 401/2009 e il regolamento (UE) 2018/1999 («European Climate Law») . 
 
 La Legge Europea sul Clima trasforma in legge l''obiettivo stabilito nel Green Deal Europeo per rendere l''economia e la società europee neutrali dal punto di vista climatico entro il 2050. La legge stabilisce anche l''obiettivo intermedio di ridurre le emissioni nette di gas serra del 55% entro il 2030, rispetto ai livelli del 1990.', 'I regolamenti sono atti giuridici definiti nell’articolo 288 del trattato sul funzionamento dell’Unione europea (TFUE). Hanno portata generale, sono vincolanti in tutti i loro elementi e direttamente applicabili negli Stati membri dell’Unione. I regolamenti rientrano nel diritto derivato dell’Unione. Sono adottati dalle istituzioni dell’Unione europea sulla base dei trattati. Un regolamento ha carattere vincolante in tutti i suoi elementi, ha portata generale ed è rivolto a categorie astratte di persone, non a specifici individui. 
 https://eur-lex.europa.eu/IT/legal-content/summary/european-union-regulations.html', 'Regolamento che istituisce il quadro per il conseguimento della neutralità climatica e che modifica il regolamento (CE) n. 401/2009 e il regolamento (UE) 2018/1999 (Legge europea sul clima)', 'Regolamento (UE) - 2021/1119', 'Regolamento (UE) 2021/1119', NULL);

-- Record 1235/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (532, 'Accordo di Parigi sui cambiamenti climatici', 'Trattato internazionale', 'Accordo di Parigi sui Cambiamenti climatici', 'L''accordo di Parigi è un trattato internazionale che presenta un piano d''azione per limitare il riscaldamento globale. L’accordo di Parigi, firmato da 194 paesi e dall’UE, mira a limitare il riscaldamento globale al di sotto di 2°C e a proseguire gli sforzi per circoscriverlo a 1,5°C al fine di evitare le conseguenze catastrofiche del cambiamento climatico.', 'L''accordo internazionale può essere considerato come un contratto stipulato tra due o più Stati, diretto a regolare una determinata sfera di rapporti tra i contraenti. La sua formazione è regolata da una serie di norme consuetudinarie e convenzionali che rientrano nel diritto pubblico internazionale.', 'Accordo sui cambiamenti climatici', 'Trattato internazionale - Accordo di Parigi sui Cambiamenti climatici', 'Accordo Parigi (2015)', 'Accordo che mira a limitare il riscaldamento globale al di sotto di 2°C');

-- Record 1236/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (532, 'Green Deal Europeo (EGD)', 'Strategia (UE)', 'Green Deal Europeo', 'Il Green Deal europeo è un pacchetto di iniziative strategiche che mira ad avviare l''UE sulla strada di una transizione verde, con l''obiettivo ultimo di raggiungere la neutralità climatica entro il 2050.', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Strategia dell''UE per conseguire l''obiettivo della neutralità climatica entro il 2050', 'Strategia (UE) - Green Deal Europeo', 'Green Deal Europeo', 'Green Deal Europeo: Strategia dell''UE per conseguire l''obiettivo della neutralità climatica entro il 2050');

-- Record 1237/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (532, 'Piano Nazionale di contabilizzazione forestale dell’Italia (2021-2025)', 'Piano Nazionale (IT)', 'Contabilizzazione forestale dell’Italia', 'Il Piano Nazionale di Contabilizzazione Forestale dell''Italia (PNCF) è un documento strategico che descrive come il paese intende monitorare, rendicontare e gestire il carbonio forestale, in conformità con gli obblighi internazionali e le politiche nazionali di mitigazione dei cambiamenti climatici. Questo piano è una parte essenziale delle politiche ambientali e climatiche italiane, poiché le foreste giocano un ruolo cruciale nel sequestro del carbonio e nella riduzione delle emissioni di gas serra.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Piano di contabilizzazione forestale dell’Italia (2021-2025)', 'Piano Nazionale (IT) - Contabilizzazione forestale dell’Italia', 'PNCFI (NFAP)', 'Piano Nazionale di contabilizzazione forestale dell’Italia');

