INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (532, 'Piano Nazionale Adattamento Cambiamenti Climatici (PNACC)', 'Piano Nazionale (IT)', 'Adattamento Cambiamento Climatico', 'La finalità del Piano è contenere la vulnerabilità dei sistemi naturali, sociali ed economici agli impatti dei cambiamenti climatici e aumentarne la resilienza. Rappresenta lo strumento di attuazione della Strategia Nazionale di adattamento ai cambiamenti climatici del 2015 per la pianificazione nazionale a supporto delle istituzioni che saranno chiamate a sviluppare sulla propria scala di governo i contenuti del piano, tenendo conto delle specificità dei diversi contesti.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Cambiamento Climatico (PNACC)', 'Piano Nazionale (IT) Adattamento Cambiamento Climatico', 'PNACC', 'Piano Nazionale Adattamento Cambiamenti Climatici');

