INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (535, 'Piano d''azione dell''UE: "Verso l''inquinamento zero per l''aria, l''acqua e il suolo"', 'Piano Azione (UE)', 'Inquinamento Zero', 'Il piano d''azione per l''inquinamento zero dell''UE, adottato nel 2021, mira a ridurre l''inquinamento atmosferico e acustico a livelli non più considerati nocivi per la salute e per gli ecosistemi naturali.', 'Un piano di azione a livello dell''Unione Europea (UE) è un documento strategico che definisce obiettivi specifici e misure concrete per affrontare sfide o perseguire opportunità a livello comunitario. Questi piani sono sviluppati e implementati dall''UE per guidare l''azione politica e normativa su questioni di interesse comune tra gli Stati membri.', 'Piano Verso l''inquinamento zero per l''aria, l''acqua e il suolo"', 'Piano Azione (UE) - Inquinamento Zero', 'Piano Azione (UE) Inquinamento Zero', 'COM(2021) 400 - Piano d''azione dell''UE: "Verso l''inquinamento zero per l''aria, l''acqua e il suolo"');

