INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (543, 'Protocollo di Kyoto della Convenzione Quadro delle Nazioni Unite sui Cambiamenti Climatici', 'Protocollo (Int.)', 'Cambiamenti Climatici (Protocollo Kyoto)', 'Il protocollo di Kyoto, che fa seguito alla convenzione quadro delle Nazioni Unite sui cambiamenti climatici, è uno dei più importanti strumenti giuridici internazionali volti a combattere i cambiamenti climatici. Esso contiene gli impegni dei paesi industrializzati a ridurre le emissioni di alcuni gas ad effetto serra, responsabili del riscaldamento del pianeta.', 'Un protocollo è un tipo di accordo formale tra Stati o altre entità sovrane che integra, modifica o specifica ulteriormente un trattato esistente. I protocolli sono strumenti giuridici che possono avere diverse funzioni e scopi, tra cui l''espansione di disposizioni di un trattato, la creazione di nuovi obblighi o la regolamentazione di questioni specifiche non completamente coperte dal trattato originale.', 'Quadro delle Nazioni Unite sui Cambiamenti Climatici', 'Protocollo - Cambiamenti Climatici (Protocollo Kyoto)', 'Protocollo Kyoto', 'Convenzione Quadro delle Nazioni Unite sui Cambiamenti Climatici');

