INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (543, 'Strategia dell''UE per ridurre le emissioni di metano (COM/2020/663)', 'Strategia (UE)', 'COM/2020/663 (Emissioni metano)', 'La Strategia dell''UE per ridurre le emissioni di metano è un''iniziativa dell''Unione Europea finalizzata a affrontare il problema delle emissioni di metano, un potente gas serra, nell''ambiente. Rappresenta un impegno significativo dell''Unione Europea verso il raggiungimento degli obiettivi climatici e dell''Accordo di Parigi, contribuendo a mitigare l''impatto del cambiamento climatico attraverso una gestione più efficiente e sostenibile delle risorse energetiche e ambientali.', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Strategia per ridurre le emissioni di metano', 'Strategia (UE) - COM/2020/663 (Emissioni metano)', 'COM/2020/663', 'Strategia dell''UE per ridurre le emissioni di metano');

-- Record 1348/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (543, 'Piano Nazionale Adattamento Cambiamenti Climatici (PNACC)', 'Piano Nazionale (IT)', 'Adattamento Cambiamento Climatico', 'La finalità del Piano è contenere la vulnerabilità dei sistemi naturali, sociali ed economici agli impatti dei cambiamenti climatici e aumentarne la resilienza. Rappresenta lo strumento di attuazione della Strategia Nazionale di adattamento ai cambiamenti climatici del 2015 per la pianificazione nazionale a supporto delle istituzioni che saranno chiamate a sviluppare sulla propria scala di governo i contenuti del piano, tenendo conto delle specificità dei diversi contesti.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Cambiamento Climatico (PNACC)', 'Piano Nazionale (IT) Adattamento Cambiamento Climatico', 'PNACC', 'Piano Nazionale Adattamento Cambiamenti Climatici');

