INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (578, 'SDGs: Goal 2 Sconfiggere la fame', 'SDGs', 'Goal 2', 'Porre fine alla fame, raggiungere la sicurezza alimentare, migliorare la nutrizione e promuovere un’agricoltura sostenibile', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Sconfiggere la fame', 'SDGs - Goal 2', 'SDG 2.1', 'Entro il 2030, porre fine alla fame e garantire a tutte le persone, in particolare ai poveri e le persone più vulnerabili, tra cui neonati, un accesso sicuro a cibo nutriente e sufficiente per tutto l''anno');

-- Record 1463/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (578, 'Direttiva (UE) 2018/851 del Parlamento europeo e del Consiglio, del 30 maggio 2018, che modifica la direttiva 2008/98/CE relativa ai rifiuti', 'Direttiva (UE)', '2018/851', 'Questa direttiva modifica la direttiva 2008/98/CE che stabilisce misure volte a proteggere l’ambiente e la salute umana evitando o riducendo la produzione di rifiuti, gli effetti negativi della produzione e della gestione dei rifiuti, riducendo gli effetti generali dell’uso delle risorse e migliorandone l’efficienza, che costituiscono elementi fondamentali per il passaggio a un’economia circolare e per assicurare la competitività a lungo termine dell’Unione.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica la direttiva 2008/98/CE relativa ai rifiuti', 'Direttiva (UE) - 2018/851', 'Direttiva (UE) 2018/851', NULL);

-- Record 1464/2644
INSERT INTO esg_impact.form_legal_bases
