INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (579, 'ESRS E2- Inquinamento', 'ESRS', 'E2', 'Questo Standard stabilisce i requisiti di Informativa GRI  relativi ai seguenti aspetti della sostenibilità: inquinamento dell''aria, dell''acqua, del suolo, sostanze preoccupanti, comprese le sostanze estremamente preoccupanti.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Inquinamento', 'ESRS - E2', 'ESRS (E2) E2-4 - 28 a  AR 21-22', 'Inquinamento dell''aria, dell''acqua e del suolo [dimensioni multiple: a livello di sito o per tipo di fonte, per settore o per area geografica]');

-- Record 1494/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (579, 'Direttiva (UE) 2022/2464 del Parlamento europeo e del Consiglio del 14 dicembre 2022 che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE)', '2022/2464', 'La CSRD o Corporate Sustainability Reporting Directive (Direttiva 2022/2464) è la direttiva europea che mira a migliorare la trasparenza e la qualità delle informazioni sulla sostenibilità fornite dalle aziende. La CSRD sostituisce e amplia la Non-Financial Reporting Directive (NFRD), aumentando le esigenze di rendicontazione per le imprese riguardo alle loro attività sostenibili. I punti chiavi della CSRD sono: -L''ampiezza del campo di applicazione rispetto alla NFRD rispetto alle aziende che devono rendicontare, - le informazioni da rendicontare in termini ESG e di sostenibilità; - lo standard di rendicontazione (gli ESRS); - L''audit e certificazione; e la digitalizzazione delle informazioni. 
 
 L''obiettivo principale della CSRD è migliorare la trasparenza e responsabilizzare le aziende verso pratiche più sostenibili, aiutando investitori, consumatori, e altre parti interessate a prendere decisioni informate basate su dati di sostenibilità accurati e completi.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE) - 2022/2464', 'CSRD', 'Rendicontazione societaria di sostenibilità (CSRD)');

-- Record 1495/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (579, 'Direttiva (UE) 2016/2284 concernente la riduzione delle emissioni nazionali di determinati inquinanti atmosferici, che modifica la direttiva 2003/35/CE e abroga la direttiva 2001/81/CE', 'Direttiva (UE)', '2016/2284', 'La direttiva stabilisce gli impegni di riduzione delle emissioni per le emissioni atmosferiche antropogeniche degli Stati membri di biossido di zolfo (SO2), ossidi di azoto (NOx), composti organici volatili non metanici (COVNM), ammoniaca (NH3), e particolato fine (PM2,5) e impone l''elaborazione, l''adozione e l''attuazione di programmi nazionali di controllo dell''inquinamento atmosferico e il monitoraggio e la comunicazione in merito ai suddetti inquinanti e agli altri inquinanti indicati all''allegato I e ai loro effetti.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva concernente alla riduzione delle emissioni nazionali di determinati inquinanti atmosferici', 'Direttiva (UE) - 2016/2284', 'Direttiva (UE) 2016/2284', NULL);

