INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (580, 'ISO 9001: Certificazione del Sistema di Gestione per la Qualità', 'ISO', '9001', 'La norma ISO 9001 specifica i requisiti per l''istituzione, il mantenimento e il miglioramento continuo di un sistema di gestione della qualità, coprendo una vasta gamma di argomenti, tra cui:
 - Contesto dell''organizzazione, - Leadership, - Pianificazione, - Supporto, - Valutazione delle prestazioni, - Miglioramento', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Certificazione del Sistema di Gestione per la Qualità', 'ISO - 9001', 'ISO 9001', 'Certificazione del Sistema di Gestione per la Qualità');

-- Record 1509/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (580, 'GRI 403- Salute e sicurezza sul lavoro (2018)', 'GRI', '403', 'Questo Standard tratta il tema della salute e sicurezza sul lavoro. Contiene informative per le organizzazioni che devono rendicontare informazioni sui loro impatti correlati alla salute e sicurezza sul lavoro e sul modo in cui li gestiscono.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Salute e sicurezza sul lavoro (2018)', 'GRI - 403 (2018)', 'GRI 403-10 (c) (2018)', '(Malattia professionale) L’organizzazione deve rendicontare le seguenti informazioni: I pericoli sul lavoro che, se non controllati, presentano un rischio di malattia
professionale [...]');

-- Record 1510/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (580, 'GRI 305- Emissioni (2016)', 'GRI', '305', 'Questo Standard tratta il tema delle emissioni nell’aria – sostanze scaricate da una fonte nell’atmosfera. I vari tipi di emissioni sono: gas a effetto serra (GHG), sostanze che riducono lo strato di ozono (ODS, ozone-depleting substances), ossidi di azoto (NO) e ossidi di zolfo (SO), oltre ad altre emissioni nell’aria di entità notevole.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Emissioni (2016)', 'GRI - 305 (2016)', 'GRI 305-7 (2016)', 'Ossidi di azoto (NOx), ossidi di zolfo (SOx) e altre emissioni nell’aria rilevanti');

-- Record 1511/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (580, 'ESRS E2- Inquinamento', 'ESRS', 'E2', 'Questo Standard stabilisce i requisiti di Informativa GRI  relativi ai seguenti aspetti della sostenibilità: inquinamento dell''aria, dell''acqua, del suolo, sostanze preoccupanti, comprese le sostanze estremamente preoccupanti.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Inquinamento', 'ESRS - E2', 'ESRS (E2) E2-4 - 28 a  AR 21-22', 'Inquinamento dell''aria, dell''acqua e del suolo [dimensioni multiple: a livello di sito o per tipo di fonte, per settore o per area geografica]');

-- Record 1512/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (580, 'Direttiva (UE) 2022/2464 del Parlamento europeo e del Consiglio del 14 dicembre 2022 che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE)', '2022/2464', 'La CSRD o Corporate Sustainability Reporting Directive (Direttiva 2022/2464) è la direttiva europea che mira a migliorare la trasparenza e la qualità delle informazioni sulla sostenibilità fornite dalle aziende. La CSRD sostituisce e amplia la Non-Financial Reporting Directive (NFRD), aumentando le esigenze di rendicontazione per le imprese riguardo alle loro attività sostenibili. I punti chiavi della CSRD sono: -L''ampiezza del campo di applicazione rispetto alla NFRD rispetto alle aziende che devono rendicontare, - le informazioni da rendicontare in termini ESG e di sostenibilità; - lo standard di rendicontazione (gli ESRS); - L''audit e certificazione; e la digitalizzazione delle informazioni. 
 
 L''obiettivo principale della CSRD è migliorare la trasparenza e responsabilizzare le aziende verso pratiche più sostenibili, aiutando investitori, consumatori, e altre parti interessate a prendere decisioni informate basate su dati di sostenibilità accurati e completi.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE) - 2022/2464', 'CSRD', 'Rendicontazione societaria di sostenibilità (CSRD)');

-- Record 1513/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (580, 'Decreto Legislativo 152/2006 - Norme in materia ambientale', 'Decreto (IT)', '152/2006', 'Il Decreto Legislativo 3 aprile 2006, n. 152 stabilisce le norme fondamentali per la tutela dell''ambiente in Italia. Questo decreto, noto come "Codice dell''ambiente", regola diversi aspetti relativi alla gestione, alla protezione e alla valorizzazione dell''ambiente.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Norme in materia ambientale', 'Decreto Legislativo - 152/2006', 'D.Lgs. 152/2006', NULL);

-- Record 1514/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (580, 'Decreto Legislativo 6 settembre 2024, n. 125 Attuazione della direttiva 2022/2464/UE del Parlamento europeo e del Consiglio del 14 dicembre 2022, recante modifica del regolamento 537/2014/UE, della direttiva 2004/109/CE, della direttiva 2006/43/CE e della direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Decreto (IT)', '125/2024', 'Il Decreto legislativo 6 settembre 2024, n. 125 attua la Direttiva 2022/2464/UE (nota come Corporate Sustainability Reporting Directive, CSRD). Questo decreto introduce nuovi obblighi di rendicontazione societaria di sostenibilità per le imprese, ampliando notevolmente le informazioni che devono essere incluse nei bilanci aziendali in materia di sostenibilità ambientale, sociale e di governance (ESG).', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Attuazione della direttiva 2022/2464/UE (CSRD)', 'Decreto Legislativo - 125/2024', 'D.Lgs. 125/2024', 'Attuazione della direttiva 2022/2464/UE (CSRD)');

-- Record 1515/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (580, 'Costituzione Italiana- Articolo 32: Principio di Diritto alla Salute', 'Costituzione Italiana', 'Articolo 32', 'L''articolo 32 della Costitutione Italiana stabilisce che: "La Repubblica tutela la salute come fondamentale diritto dell’individuo e interesse della collettività, e garantisce cure gratuite agli indigenti. Nessuno può essere obbligato a un determinato trattamento sanitario se non per disposizione di legge. La legge non può in nessun caso violare i limiti imposti dal rispetto della persona umana".', 'La Costituzione Italiana è la legge fondamentale della Repubblica Italiana. Essa stabilisce i principi fondamentali su cui si basa l''ordinamento dello Stato italiano, definendo i diritti e i doveri dei cittadini, l''organizzazione e i poteri delle istituzioni statali (Parlamento, Governo e Magistratura), nonché i principi economici e sociali dello Stato. La Costituzione è la norma suprema, alla quale devono conformarsi tutte le altre leggi e normative del Paese.', 'Principio di Diritto alla Salute', 'Costituzione Italiana - Articolo 32', 'Art. 32 Cost.', 'La Repubblica tutela la salute come fondamentale diritto dell’individuo e interesse della collettività, e garantisce cure gratuite agli indigenti. Nessuno può essere obbligato a un determinato trattamento sanitario se non per disposizione di legge. La legge non può in nessun caso violare i limiti imposti dal rispetto della persona umana.');

-- Record 1516/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (581, 'SDGs: Goal 4 Istruzione di qualità', 'SDGs', 'Goal 4', 'Fornire un’educazione di qualità, equa ed inclusiva, e opportunità di apprendimento per tutti', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Istruzione di qualità', 'SDGs - Goal 4', 'SDG 4.4', 'Aumentare considerevolmente entro il 2030 il numero di giovani e adulti con competenze specifiche -anche tecniche e professionali- per l’occupazione, posti di lavoro dignitosi e per l’imprenditoria');

-- Record 1517/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (581, 'GRI 404- Formazione e istruzione (2016)', 'GRI', '404', 'Questo Standard tratta il tema dell’istruzione e della formazione. Tale tema riguarda l’approccio di un’organizzazione alla formazione e all’aggiornamento delle competenze dei dipendenti, nonché a valutazioni sulle prestazioni e il percorso professionale. Comprende anche programmi di assistenza alla transizione, al fine di agevolare l’occupabilità e la gestione della fine del percorso lavorativo per motivi di pensionamento o conclusione del rapporto.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Formazione e istruzione (2016)', 'GRI - 404 (2016)', 'GRI 404-1 (2016)', 'Numero medio di ore di formazione all’anno per dipendente');

-- Record 1518/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (581, 'SDGs: Goal 8 Lavoro dignitoso e crescita economica', 'SDGs', 'Goal 8', 'Incentivare una crescita economica duratura, inclusiva e sostenibile, un’occupazione piena e produttiva ed un lavoro dignitoso per tutti', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Lavoro dignitoso e crescita economica', 'SDGs - Goal 8', 'SDG 8.5', 'Garantire entro il 2030 un’occupazione piena e produttiva e un lavoro dignitoso per donne e uomini, compresi i giovani e le persone con disabilità, e un’equa remunerazione per lavori di equo valore');

-- Record 1519/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (581, 'WEF- Metriche del Capitalismo degli Stakeholder 2020 P3-3 (Competenze per il futuro)', 'WEF', '4P: Metrica P3-3', 'L''accesso a lavoratori qualificati è cruciale per il successo aziendale. Investire in formazione, riqualificazione e cultura inclusiva migliora il coinvolgimento, la produttività e l''adattamento ai cambiamenti tecnologici e lavorativi.', 'Il World Economic Forum è l''Organizzazione Internazionale per la Cooperazione Pubblico-Privata. L''WEF insieme ad altre istituzioni ha sviluppato un insieme di metriche comuni per la rendicontazione della creazione di valore sostenibile (21 metriche principali e 34 metriche e divulgazioni ampliate) raggruprate in 4 pilastri principali (Principle of Governance, Planet, Persone and Prosperity). Per maggiore informazioni sulle 4 Pilastri (Metriche) di sostenibilità del WEF andare su: https://www.weforum.org/stakeholdercapitalism/our-metrics/', '3- Competenze per il futuro', 'WEF (MSC2024) 4P- P3-3', 'WEF (MSC2024) 4P- P3-3 CM 1', 'Formazione fornita');

-- Record 1520/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (582, 'C100 - Convenzione sull’uguaglianza di retribuzione, 1951 - ILO', 'ILO', 'Convenzione 100', 'La Convenzione 100 dell''Organizzazione Internazionale del Lavoro (ILO), nota anche come Convenzione sull''uguaglianza di retribuzione, è uno strumento internazionale adottato nel 1951 che mira a garantire la parità di retribuzione tra uomini e donne per un lavoro di pari valore.', 'L''Organizzazione Internazionale del Lavoro (OIL), fondata nel 1919, è un''agenzia specializzata delle Nazioni Unite che promuove i diritti nel lavoro, opportunità di occupazione dignitosa, protezione sociale e il dialogo sulle questioni lavorative. L’OIL definisce standard internazionali attraverso convenzioni e raccomandazioni, contribuendo a sostenere la giustizia sociale e i diritti umani nel mondo del lavoro. Un elemento distintivo è il suo approccio tripartito, che coinvolge governi, datori di lavoro e lavoratori per sviluppare politiche e programmi efficaci.

Gli standard internazionali del lavoro, elaborati dai costituenti dell''OIL, stabiliscono principi e diritti fondamentali nel lavoro. Questi strumenti si suddividono in: -Convenzioni e Protocolli, trattati internazionali legalmente vincolanti che possono essere ratificati dagli Stati membri; -Raccomandazioni, che offrono linee guida non vincolanti per orientare le politiche nazionali.
Per maggiore informazione vedere il sito: https://www.ilo.org/international-labour-standards', 'Sull’uguaglianza di retribuzione -ILO', 'Convenzione 100 - ILO', 'ILO- C100', 'Convenzione 100, ILO – mira a garantire la parità di retribuzione tra uomini e donne per un lavoro di pari valore');

-- Record 1521/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (582, 'C111 - Convenzione sulla discriminazione (impiego e professione), 1958 - ILO', 'ILO', 'Convenzione 111', 'La Convenzione 111 dell''Organizzazione Internazionale del Lavoro (ILO), adottata nel 1958, è conosciuta come la Convenzione sulla discriminazione (impiego e professione). Questa convenzione mira a combattere la discriminazione nel campo dell''occupazione e della professione e a promuovere la parità di opportunità e di trattamento.', 'L''Organizzazione Internazionale del Lavoro (OIL), fondata nel 1919, è un''agenzia specializzata delle Nazioni Unite che promuove i diritti nel lavoro, opportunità di occupazione dignitosa, protezione sociale e il dialogo sulle questioni lavorative. L’OIL definisce standard internazionali attraverso convenzioni e raccomandazioni, contribuendo a sostenere la giustizia sociale e i diritti umani nel mondo del lavoro. Un elemento distintivo è il suo approccio tripartito, che coinvolge governi, datori di lavoro e lavoratori per sviluppare politiche e programmi efficaci.

Gli standard internazionali del lavoro, elaborati dai costituenti dell''OIL, stabiliscono principi e diritti fondamentali nel lavoro. Questi strumenti si suddividono in: -Convenzioni e Protocolli, trattati internazionali legalmente vincolanti che possono essere ratificati dagli Stati membri; -Raccomandazioni, che offrono linee guida non vincolanti per orientare le politiche nazionali.
Per maggiore informazione vedere il sito: https://www.ilo.org/international-labour-standards', 'Sulla discriminazione (impiego e professione)', 'Convenzione 111 - ILO', 'ILO- C111', 'Convenzione 111, ILO – mira a combattere la discriminazione nel campo dell''occupazione e della professione e a promuovere la parità di opportunità e di trattamento');

-- Record 1522/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (582, 'Convenzione  sull’eliminazione di tutte le forme di discriminazione contro le donne (CEDAW) - 1979', 'Convenzione (Int.)', 'Convenzione CEDAW', 'La Convenzione fornisce la base per realizzare la parità tra la donna e l’uomo, assicurando pari accesso e pari opportunità alle donne nella vita politica e pubblica – tra cui il diritto di voto e di eleggibilità - così come nei settori dell’istruzione, della salute e dell’occupazione.', 'Una Convenzione internazionale è un trattato o un accordo formale stipulato tra Stati sovrani o organizzazioni internazionali, che stabilisce obblighi legali e normativi per i firmatari in relazione a specifici argomenti o settori. Queste convenzioni sono create con l''obiettivo di promuovere la cooperazione internazionale, stabilire standard comuni, e affrontare questioni di interesse globale come i diritti umani, l''ambiente, il commercio, la sicurezza e altre tematiche di rilevanza internazionale.', 'Eliminazione di tutte le forme di discriminazione contro le donne', 'Convenzione int. - CEDAW - (1979)', 'Convenzione CEDAW', 'Convenzione int. - CEDAW - (1979) - Eliminazione di tutte le forme di discriminazione contro le donne');

-- Record 1523/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (582, 'Costituzione Italiana- Articolo 3: Principio dell''eguaglianza formale e sostanziale', 'Costituzione Italiana', 'Articolo 3', 'L''articolo 3 della Costituzione Italiana stabilisce che: "Tutti i cittadini hanno pari dignità sociale e sono eguali davanti alla legge, senza distinzione di sesso, di razza, di lingua, di religione, di opinioni politiche, di condizioni personali e sociali. È compito della Repubblica rimuovere gli ostacoli di ordine economico e sociale, che, limitando di fatto la libertà e l’eguaglianza dei cittadini, impediscono il pieno sviluppo della persona umana e l’effettiva partecipazione di tutti i lavoratori all’organizzazione politica, economica e sociale del Paese".', 'La Costituzione Italiana è la legge fondamentale della Repubblica Italiana. Essa stabilisce i principi fondamentali su cui si basa l''ordinamento dello Stato italiano, definendo i diritti e i doveri dei cittadini, l''organizzazione e i poteri delle istituzioni statali (Parlamento, Governo e Magistratura), nonché i principi economici e sociali dello Stato. La Costituzione è la norma suprema, alla quale devono conformarsi tutte le altre leggi e normative del Paese.', 'Principio dell''eguaglianza formale e sostanziale', 'Costituzione Italiana - Articolo 3', 'Art. 3 Cost.', 'Tutti i cittadini hanno pari dignità sociale e sono eguali davanti alla legge, senza distinzione di sesso, di razza, di lingua, di religione, di opinioni politiche, di condizioni personali e sociali. È compito della Repubblica rimuovere gli ostacoli di ordine economico e sociale, che, limitando di fatto la libertà e l’eguaglianza dei cittadini, impediscono il pieno sviluppo della persona umana e l’effettiva partecipazione di tutti i lavoratori all’organizzazione politica, economica e sociale del Paese.');

