INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (582, 'ESRS S1- Forza lavoro propria', 'ESRS', 'S1', 'L''obiettivo di questo Standard è specificare i requisiti di divulgazione che consentiranno agli utenti della dichiarazione di sostenibilità di comprendere gli impatti materiali dell''azienda sul proprio personale, nonché i rischi e le opportunità materiali correlati.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Forza lavoro propria', 'ESRS - S1', 'ESRS (S1) S1-16 97a AR 98-AR 99-AR 100', 'Le informazioni richieste dal paragrafo 95 devono includere: (a) il divario retributivo di genere, definito come la differenza nei livelli di retribuzione media tra dipendenti di sesso femminile e maschile, espressa come percentuale del livello retributivo medio dei dipendenti di sesso maschile.');

-- Record 1534/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (582, 'Global Compact delle Nazioni Unite (UNGC): Principio 1- Diritti Umani', 'UNGC', 'Principio 1', 'Le imprese dovrebbero sostenere e rispettare la protezione dei diritti umani proclamati a livello internazionale', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Diritti Umani', 'UNGC - Principio 1', 'UNGC P.1', 'Le imprese dovrebbero sostenere e rispettare la protezione dei diritti umani proclamati a livello internazionale');

