INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (582, 'GRI 405- Diversità e pari opportunità (2016)', 'GRI', '405', 'Questo Standard tratta il tema dell’approccio dell’organizzazione riguardo alla diversità e alle pari opportunità sul lavoro. Contiene informative per le organizzazioni che devono rendicontare informazioni sui loro impatti relativamente a diversità e pari opportunità sul lavoro, e su come li gestiscono.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Diversità e pari opportunità (2016)', 'GRI - 405 (2016)', 'GRI 405-2 (2016)', 'Rapporto tra salario di base e retribuzione delle donne rispetto agli uomini');

-- Record 1538/2644
