INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (582, 'Linee guida dell''OCSE per le imprese multinazionali sulla condotta responsabile delle imprese: Principio 2', 'OECD', 'Linee guida - Principio 2', 'Il principio 2 delle Linee Guida dell''OCSE stabilisce che le imprese dovrebbero: Rispettare i diritti umani internazionalmente riconosciuti delle persone interessate dalle loro attività.', 'Le Linee guida dell''OCSE per le imprese multinazionali sono raccomandazioni governative rivolte alle aziende, volte a promuovere una condotta responsabile. L''obiettivo è incentivare contributi positivi al progresso economico, ambientale e sociale, riducendo al contempo gli impatti negativi legati alle operazioni, ai prodotti e ai servizi aziendali. Le Linee guida coprono tutte le principali aree di responsabilità d''impresa, tra cui diritti umani, diritti dei lavoratori, ambiente, corruzione, interessi dei consumatori, trasparenza, scienza e tecnologia, concorrenza e fiscalità.', 'Linee guida per le imprese multinazionali sulla condotta responsabile delle imprese', 'OECD - Linee guida - Principio 2', 'Linee guida OECD P.2', 'Rispettare i diritti umani internazionalmente riconosciuti delle persone interessate dalle loro attività.');

-- Record 1542/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (582, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 11: La responsabilità di rispettare', 'UNGPs', 'Principio 11', 'Le imprese dovrebbero rispettare i diritti umani. Questo significa che esse dovrebbero evitare di violare i diritti umani di altre persone e affrontare gli impatti negativi sui diritti umani in cui esse siano coinvolte', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'La responsabilità di rispettare', 'UNGPs - Principio 11', 'UNGPs P.11', 'Le imprese dovrebbero rispettare i diritti umani. Questo significa che esse dovrebbero evitare di violare i diritti umani di altre persone e affrontare gli impatti negativi sui diritti umani in cui esse siano coinvolte');

-- Record 1543/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (582, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 12: I diritti umani internazionalmente riconosciuti', 'UNGPs', 'Principio 12', 'La responsabilità delle imprese di rispettare i diritti umani si riferisce ai diritti umani internazionalmente riconosciuti – intesi, come minimo, quelli espressi nella Carta Internazionale dei Diritti Umani e i principi concernenti i diritti fondamentali enunciati nella Dichiarazione sui Principi e i Diritti Fondamentali nel Lavoro dell’Organizzazione Internazionale del Lavoro', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'I diritti umani internazionalmente riconosciuti', 'UNGPs - Principio 12', 'UNGPs P.12', 'La responsabilità delle imprese di rispettare i diritti umani si riferisce ai diritti umani internazionalmente riconosciuti – intesi, come minimo, quelli espressi nella Carta Internazionale dei Diritti Umani e i principi concernenti i diritti fondamentali enunciati nella Dichiarazione sui Principi e i Diritti Fondamentali nel Lavoro dell’Organizzazione Internazionale del Lavoro');

