INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (586, 'C183 - Convenzione sulla protezione della maternità, 2000 - ILO', 'ILO', 'Convenzione 183', 'La Convenzione n. 183 sulla protezione della maternità è uno strumento normativo internazionale adottato dall''Organizzazione Internazionale del Lavoro (OIL) nel 2000. Questa convenzione aggiorna e sostituisce le precedenti norme sulla protezione della maternità, stabilendo nuovi standard per migliorare la tutela delle lavoratrici durante la gravidanza e dopo il parto.', 'L''Organizzazione Internazionale del Lavoro (OIL), fondata nel 1919, è un''agenzia specializzata delle Nazioni Unite che promuove i diritti nel lavoro, opportunità di occupazione dignitosa, protezione sociale e il dialogo sulle questioni lavorative. L’OIL definisce standard internazionali attraverso convenzioni e raccomandazioni, contribuendo a sostenere la giustizia sociale e i diritti umani nel mondo del lavoro. Un elemento distintivo è il suo approccio tripartito, che coinvolge governi, datori di lavoro e lavoratori per sviluppare politiche e programmi efficaci.

Gli standard internazionali del lavoro, elaborati dai costituenti dell''OIL, stabiliscono principi e diritti fondamentali nel lavoro. Questi strumenti si suddividono in: -Convenzioni e Protocolli, trattati internazionali legalmente vincolanti che possono essere ratificati dagli Stati membri; -Raccomandazioni, che offrono linee guida non vincolanti per orientare le politiche nazionali.
Per maggiore informazione vedere il sito: https://www.ilo.org/international-labour-standards', 'Sulla protezione della maternità', 'Convenzione 183 - ILO', 'ILO- C183', 'Convenzione 183, ILO – stabilisce i nuovi standard per migliorare la tutela delle lavoratrici durante la gravidanza e dopo il parto');

-- Record 1601/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (586, 'Convenzione  sull’eliminazione di tutte le forme di discriminazione contro le donne (CEDAW) - 1979', 'Convenzione (Int.)', 'Convenzione CEDAW', 'La Convenzione fornisce la base per realizzare la parità tra la donna e l’uomo, assicurando pari accesso e pari opportunità alle donne nella vita politica e pubblica – tra cui il diritto di voto e di eleggibilità - così come nei settori dell’istruzione, della salute e dell’occupazione.', 'Una Convenzione internazionale è un trattato o un accordo formale stipulato tra Stati sovrani o organizzazioni internazionali, che stabilisce obblighi legali e normativi per i firmatari in relazione a specifici argomenti o settori. Queste convenzioni sono create con l''obiettivo di promuovere la cooperazione internazionale, stabilire standard comuni, e affrontare questioni di interesse globale come i diritti umani, l''ambiente, il commercio, la sicurezza e altre tematiche di rilevanza internazionale.', 'Eliminazione di tutte le forme di discriminazione contro le donne', 'Convenzione int. - CEDAW - (1979)', 'Convenzione CEDAW', 'Convenzione int. - CEDAW - (1979) - Eliminazione di tutte le forme di discriminazione contro le donne');

-- Record 1602/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (586, 'Costituzione Italiana- Articolo 3: Principio dell''eguaglianza formale e sostanziale', 'Costituzione Italiana', 'Articolo 3', 'L''articolo 3 della Costituzione Italiana stabilisce che: "Tutti i cittadini hanno pari dignità sociale e sono eguali davanti alla legge, senza distinzione di sesso, di razza, di lingua, di religione, di opinioni politiche, di condizioni personali e sociali. È compito della Repubblica rimuovere gli ostacoli di ordine economico e sociale, che, limitando di fatto la libertà e l’eguaglianza dei cittadini, impediscono il pieno sviluppo della persona umana e l’effettiva partecipazione di tutti i lavoratori all’organizzazione politica, economica e sociale del Paese".', 'La Costituzione Italiana è la legge fondamentale della Repubblica Italiana. Essa stabilisce i principi fondamentali su cui si basa l''ordinamento dello Stato italiano, definendo i diritti e i doveri dei cittadini, l''organizzazione e i poteri delle istituzioni statali (Parlamento, Governo e Magistratura), nonché i principi economici e sociali dello Stato. La Costituzione è la norma suprema, alla quale devono conformarsi tutte le altre leggi e normative del Paese.', 'Principio dell''eguaglianza formale e sostanziale', 'Costituzione Italiana - Articolo 3', 'Art. 3 Cost.', 'Tutti i cittadini hanno pari dignità sociale e sono eguali davanti alla legge, senza distinzione di sesso, di razza, di lingua, di religione, di opinioni politiche, di condizioni personali e sociali. È compito della Repubblica rimuovere gli ostacoli di ordine economico e sociale, che, limitando di fatto la libertà e l’eguaglianza dei cittadini, impediscono il pieno sviluppo della persona umana e l’effettiva partecipazione di tutti i lavoratori all’organizzazione politica, economica e sociale del Paese.');

-- Record 1603/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (586, 'Costituzione Italiana: Articolo 37: Parità tra uomo e donna in materia di lavoro', 'Costituzione Italiana', 'Articolo 37', 'La donna lavoratrice ha gli stessi diritti e, a parità di lavoro, le stesse retribuzioni che spettano al lavoratore. Le condizioni di lavoro devono consentire l’adempimento della sua essenziale funzione familiare e assicurare alla madre e al bambino una speciale adeguata protezione.', 'La Costituzione Italiana è la legge fondamentale della Repubblica Italiana. Essa stabilisce i principi fondamentali su cui si basa l''ordinamento dello Stato italiano, definendo i diritti e i doveri dei cittadini, l''organizzazione e i poteri delle istituzioni statali (Parlamento, Governo e Magistratura), nonché i principi economici e sociali dello Stato. La Costituzione è la norma suprema, alla quale devono conformarsi tutte le altre leggi e normative del Paese.', 'Parità tra uomo e donna in materia di lavoro', 'Costituzione Italiana - Articolo 37', 'Art. 37 Cost.', 'Parità tra uomo e donna in materia di lavoro');

-- Record 1604/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (586, 'Decreto Legislativo 151/2001 - Testo unico delle disposizioni legislative in materia di tutela e sostegno della maternità e della paternità, a norma dell''articolo 15 della legge 8 marzo 2000, n. 53.', 'Decreto (IT)', '151/2001', 'Il Decreto Legislativo 151/2001, noto anche come "Testo Unico delle disposizioni legislative in materia di tutela e sostegno della maternità e della paternità", è una normativa italiana che raccoglie e coordina le leggi esistenti in materia di protezione della maternità e della paternità, nonché le disposizioni relative al congedo parentale. Questo decreto rappresenta un importante riferimento legislativo per la tutela dei diritti dei genitori lavoratori in Italia.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Disposizioni legislative in materia di tutela e sostegno della maternità e della paternità', 'Decreto Legislativo - 151/2001', 'D.Lgs. 151/2001', NULL);

-- Record 1605/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (586, 'Decreto Legislativo 198/2006 - Codice delle pari opportunità tra uomo e donna, a norma dell''articolo 6 della legge 28 novembre 2005, n. 246.', 'Decreto (IT)', '198/2006', 'Il decreto stabilisce le misure volte ad eliminare ogni discriminazione basata sul sesso, che abbia come conseguenza o come scopo di compromettere o di impedire il riconoscimento, il godimento o l''esercizio dei diritti umani e delle libertà fondamentali in campo politico, economico, sociale, culturale e civile o in ogni altro campo.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Codice delle pari opportunità tra uomo e donna', 'Decreto Legislativo - 198/2006', 'D.Lgs. 198/2006', NULL);

-- Record 1606/2644
INSERT INTO esg_impact.form_legal_bases
