INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (587, 'Global Compact delle Nazioni Unite (UNGC): Principio 1- Diritti Umani', 'UNGC', 'Principio 1', 'Le imprese dovrebbero sostenere e rispettare la protezione dei diritti umani proclamati a livello internazionale', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Diritti Umani', 'UNGC - Principio 1', 'UNGC P.1', 'Le imprese dovrebbero sostenere e rispettare la protezione dei diritti umani proclamati a livello internazionale');

