INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (587, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 14: Dimensione, settore, contesto operativo, assetto proprietario e struttura dell’impresa', 'UNGPs', 'Principio 14', 'La responsabilità delle imprese di rispettare i diritti umani si applica a tutte le imprese indipendentemente dalla loro dimensione, dal settore, dal contesto operativo, dell’assetto proprietario e dalla loro struttura. Nondimeno, la portata e la complessità degli strumenti con cui le imprese assolvono tale responsabilità possono variare in base ai summenzionati fattori e al livello di gravità degli impatti negativi dell’impresa sui diritti umani.', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'Dimensione, settore, contesto operativo, assetto proprietario e struttura dell’impresa', 'UNGPs - Principio 14', 'UNGPs P.14', 'La responsabilità delle imprese di rispettare i diritti umani si applica a tutte le imprese indipendentemente dalla loro dimensione, dal settore, dal contesto operativo, dell’assetto proprietario e dalla loro struttura. Nondimeno, la portata e la complessità degli strumenti con cui le imprese assolvono tale responsabilità possono variare in base ai summenzionati fattori e al livello di gravità degli impatti negativi dell’impresa sui diritti umani.');

