INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (588, 'GRI 2- Informativa generale (2021)', 'GRI', '2', 'GRI che contiene le informative per le organizzazioni che devono fornire informazioni sulle loro prassi di rendicontazione, sulle attività e sui lavoratori, sulla governance, sulla strategia, sulle politiche e sulle  pratiche e sul coinvolgimento degli stakeholder', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Informativa generale (2021)', 'GRI - 2 (2021)', 'GRI 2-25 (2021)', 'Processi volti a rimediare agli impatti negativi');

-- Record 1682/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (588, 'Linee guida dell''OCSE per le imprese multinazionali sulla condotta responsabile delle imprese: Principio 2', 'OECD', 'Linee guida - Principio 2', 'Il principio 2 delle Linee Guida dell''OCSE stabilisce che le imprese dovrebbero: Rispettare i diritti umani internazionalmente riconosciuti delle persone interessate dalle loro attività.', 'Le Linee guida dell''OCSE per le imprese multinazionali sono raccomandazioni governative rivolte alle aziende, volte a promuovere una condotta responsabile. L''obiettivo è incentivare contributi positivi al progresso economico, ambientale e sociale, riducendo al contempo gli impatti negativi legati alle operazioni, ai prodotti e ai servizi aziendali. Le Linee guida coprono tutte le principali aree di responsabilità d''impresa, tra cui diritti umani, diritti dei lavoratori, ambiente, corruzione, interessi dei consumatori, trasparenza, scienza e tecnologia, concorrenza e fiscalità.', 'Linee guida per le imprese multinazionali sulla condotta responsabile delle imprese', 'OECD - Linee guida - Principio 2', 'Linee guida OECD P.2', 'Rispettare i diritti umani internazionalmente riconosciuti delle persone interessate dalle loro attività.');

-- Record 1683/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (588, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 11: La responsabilità di rispettare', 'UNGPs', 'Principio 11', 'Le imprese dovrebbero rispettare i diritti umani. Questo significa che esse dovrebbero evitare di violare i diritti umani di altre persone e affrontare gli impatti negativi sui diritti umani in cui esse siano coinvolte', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'La responsabilità di rispettare', 'UNGPs - Principio 11', 'UNGPs P.11', 'Le imprese dovrebbero rispettare i diritti umani. Questo significa che esse dovrebbero evitare di violare i diritti umani di altre persone e affrontare gli impatti negativi sui diritti umani in cui esse siano coinvolte');

-- Record 1684/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (588, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 12: I diritti umani internazionalmente riconosciuti', 'UNGPs', 'Principio 12', 'La responsabilità delle imprese di rispettare i diritti umani si riferisce ai diritti umani internazionalmente riconosciuti – intesi, come minimo, quelli espressi nella Carta Internazionale dei Diritti Umani e i principi concernenti i diritti fondamentali enunciati nella Dichiarazione sui Principi e i Diritti Fondamentali nel Lavoro dell’Organizzazione Internazionale del Lavoro', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'I diritti umani internazionalmente riconosciuti', 'UNGPs - Principio 12', 'UNGPs P.12', 'La responsabilità delle imprese di rispettare i diritti umani si riferisce ai diritti umani internazionalmente riconosciuti – intesi, come minimo, quelli espressi nella Carta Internazionale dei Diritti Umani e i principi concernenti i diritti fondamentali enunciati nella Dichiarazione sui Principi e i Diritti Fondamentali nel Lavoro dell’Organizzazione Internazionale del Lavoro');

-- Record 1685/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (588, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 13: Causa, contributo e collegamento diretto', 'UNGPs', 'Principio 13', 'La responsabilità di rispettare i diritti umani richiede che le imprese: a) evitino di causare o contribuire con le proprie attività agli impatti negativi sui diritti umani, e prendano le misure opportune quando tali impatti si verificano;  
b) si impegnino per prevenire o mitigare gli impatti negativi sui diritti umani direttamente collegati alle loro operazioni, ai loro prodotti o ai loro servizi in virtù di una relazione commerciale, anche se esse non abbiano contribuito a tali impatti.', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'Causa, contributo e collegamento diretto', 'UNGPs - Principio 13', 'UNGPs P.13', 'La responsabilità di rispettare i diritti umani richiede che le imprese: a) evitino di causare o contribuire con le proprie attività agli impatti negativi sui diritti umani, e prendano le misure opportune quando tali impatti si verificano; 
b) si impegnino per prevenire o mitigare gli impatti negativi sui diritti umani direttamente collegati alle loro operazioni, ai loro prodotti o ai loro servizi in virtù di una relazione commerciale, anche se esse non abbiano contribuito a tali impatti.');

-- Record 1686/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (588, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 14: Dimensione, settore, contesto operativo, assetto proprietario e struttura dell’impresa', 'UNGPs', 'Principio 14', 'La responsabilità delle imprese di rispettare i diritti umani si applica a tutte le imprese indipendentemente dalla loro dimensione, dal settore, dal contesto operativo, dell’assetto proprietario e dalla loro struttura. Nondimeno, la portata e la complessità degli strumenti con cui le imprese assolvono tale responsabilità possono variare in base ai summenzionati fattori e al livello di gravità degli impatti negativi dell’impresa sui diritti umani.', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'Dimensione, settore, contesto operativo, assetto proprietario e struttura dell’impresa', 'UNGPs - Principio 14', 'UNGPs P.14', 'La responsabilità delle imprese di rispettare i diritti umani si applica a tutte le imprese indipendentemente dalla loro dimensione, dal settore, dal contesto operativo, dell’assetto proprietario e dalla loro struttura. Nondimeno, la portata e la complessità degli strumenti con cui le imprese assolvono tale responsabilità possono variare in base ai summenzionati fattori e al livello di gravità degli impatti negativi dell’impresa sui diritti umani.');

