INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (588, 'Strategia Nazionale per lo Sviluppo Sostenibile (SNSvS)', 'Strategia Nazionale (IT)', 'SNSvS', 'In Italia rappresenta il quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali, declinando per il contesto nazionale gli Obiettivi di Sviluppo Sostenibile (SDG) delineati dall''ONU', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia Nazionale italiana per lo Sviluppo Sostenibile', 'SNSvS (2022)', 'SNSvS22- Prosperità IV.3', 'Promuovere la responsabilità sociale, ambientale e dei diritti umani nelle amministrazioni e nelle imprese, anche attraverso la finanza sostenibile');

-- Record 1688/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (589, 'SDGs: Goal 8 Lavoro dignitoso e crescita economica', 'SDGs', 'Goal 8', 'Incentivare una crescita economica duratura, inclusiva e sostenibile, un’occupazione piena e produttiva ed un lavoro dignitoso per tutti', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Lavoro dignitoso e crescita economica', 'SDGs - Goal 8', 'SDG 8.5', 'Garantire entro il 2030 un’occupazione piena e produttiva e un lavoro dignitoso per donne e uomini, compresi i giovani e le persone con disabilità, e un’equa remunerazione per lavori di equo valore');

-- Record 1689/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (589, 'Costituzione Italiana- Articolo 36: Rapporti economici', 'Costituzione Italiana', 'Articolo 36', 'L''articolo 36 della Costituzione Italiana stabilisce che: "Il lavoratore ha diritto ad una retribuzione proporzionata alla quantità e qualità del suo lavoro e in ogni caso sufficiente ad assicurare a sé e alla famiglia un''esistenza libera e dignitosa. La durata massima della giornata lavorativa è stabilita dalla legge. Il lavoratore ha diritto al riposo settimanale e a ferie annuali retribuite, e non può rinunziarvi".', 'La Costituzione Italiana è la legge fondamentale della Repubblica Italiana. Essa stabilisce i principi fondamentali su cui si basa l''ordinamento dello Stato italiano, definendo i diritti e i doveri dei cittadini, l''organizzazione e i poteri delle istituzioni statali (Parlamento, Governo e Magistratura), nonché i principi economici e sociali dello Stato. La Costituzione è la norma suprema, alla quale devono conformarsi tutte le altre leggi e normative del Paese.', 'Retribuzione proporcionata al lavoro', 'Costituzione Italiana - Articolo 36', 'Art. 36 Cost.', 'Art. 36: Il lavoratore ha diritto ad una retribuzione proporzionata alla quantità e qualità del suo lavoro e in ogni caso sufficiente ad assicurare a sé e alla famiglia un''esistenza libera e dignitosa. (...).');

