INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (591, 'ISO 45001: Sistemi di gestione per la salute e sicurezza sul lavoro', 'ISO', '45001', 'ISO 45001 è uno standard globale che definisce i requisiti per gestire la salute e la sicurezza sul lavoro. Aiuta le organizzazioni a gestire i rischi e migliorare le loro performance in quest''area.
 
 L''ISO 45001 stabilisce i criteri per una politica di salute e sicurezza sul lavoro, obiettivi, pianificazione, implementazione, operazioni, audit e revisione. Gli elementi principali comprendono l''engagement della leadership, la partecipazione dei lavoratori, l''identificazione dei rischi, la conformità legale, la pianificazione delle emergenze, l''indagine sugli incidenti e il miglioramento continuo.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Sistemi di gestione per la salute e sicurezza sul lavoro', 'ISO - 45001', 'ISO 45001', 'Sistemi di gestione per la salute e sicurezza sul lavoro');

-- Record 1714/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (591, 'Linee guida dell''OCSE per le imprese multinazionali sulla condotta responsabile delle imprese: Principio 2', 'OECD', 'Linee guida - Principio 2', 'Il principio 2 delle Linee Guida dell''OCSE stabilisce che le imprese dovrebbero: Rispettare i diritti umani internazionalmente riconosciuti delle persone interessate dalle loro attività.', 'Le Linee guida dell''OCSE per le imprese multinazionali sono raccomandazioni governative rivolte alle aziende, volte a promuovere una condotta responsabile. L''obiettivo è incentivare contributi positivi al progresso economico, ambientale e sociale, riducendo al contempo gli impatti negativi legati alle operazioni, ai prodotti e ai servizi aziendali. Le Linee guida coprono tutte le principali aree di responsabilità d''impresa, tra cui diritti umani, diritti dei lavoratori, ambiente, corruzione, interessi dei consumatori, trasparenza, scienza e tecnologia, concorrenza e fiscalità.', 'Linee guida per le imprese multinazionali sulla condotta responsabile delle imprese', 'OECD - Linee guida - Principio 2', 'Linee guida OECD P.2', 'Rispettare i diritti umani internazionalmente riconosciuti delle persone interessate dalle loro attività.');

