INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (597, 'GRI 2- Informativa generale (2021)', 'GRI', '2', 'GRI che contiene le informative per le organizzazioni che devono fornire informazioni sulle loro prassi di rendicontazione, sulle attività e sui lavoratori, sulla governance, sulla strategia, sulle politiche e sulle  pratiche e sul coinvolgimento degli stakeholder', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Informativa generale (2021)', 'GRI - 2 (2021)', 'GRI 2-25 (2021)', 'Processi volti a rimediare agli impatti negativi');

-- Record 1761/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (597, 'ISO 26000: responsabilità sociale delle organizzazioni', 'ISO', '26000', 'Fornisce linee guida su come le imprese e le organizzazioni possano operare in modo etico e trasparente, contribuendo allo sviluppo sostenibile e tenendo conto delle aspettative degli stakeholder, delle leggi applicabili e delle norme internazionali di comportamento.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Responsabilità sociale delle organizzazioni', 'ISO - 26000', 'ISO 26000', 'Responsabilità sociale delle organizzazioni');

-- Record 1762/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (597, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 11: La responsabilità di rispettare', 'UNGPs', 'Principio 11', 'Le imprese dovrebbero rispettare i diritti umani. Questo significa che esse dovrebbero evitare di violare i diritti umani di altre persone e affrontare gli impatti negativi sui diritti umani in cui esse siano coinvolte', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'La responsabilità di rispettare', 'UNGPs - Principio 11', 'UNGPs P.11', 'Le imprese dovrebbero rispettare i diritti umani. Questo significa che esse dovrebbero evitare di violare i diritti umani di altre persone e affrontare gli impatti negativi sui diritti umani in cui esse siano coinvolte');

-- Record 1763/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (597, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 12: I diritti umani internazionalmente riconosciuti', 'UNGPs', 'Principio 12', 'La responsabilità delle imprese di rispettare i diritti umani si riferisce ai diritti umani internazionalmente riconosciuti – intesi, come minimo, quelli espressi nella Carta Internazionale dei Diritti Umani e i principi concernenti i diritti fondamentali enunciati nella Dichiarazione sui Principi e i Diritti Fondamentali nel Lavoro dell’Organizzazione Internazionale del Lavoro', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'I diritti umani internazionalmente riconosciuti', 'UNGPs - Principio 12', 'UNGPs P.12', 'La responsabilità delle imprese di rispettare i diritti umani si riferisce ai diritti umani internazionalmente riconosciuti – intesi, come minimo, quelli espressi nella Carta Internazionale dei Diritti Umani e i principi concernenti i diritti fondamentali enunciati nella Dichiarazione sui Principi e i Diritti Fondamentali nel Lavoro dell’Organizzazione Internazionale del Lavoro');

-- Record 1764/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (597, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 13: Causa, contributo e collegamento diretto', 'UNGPs', 'Principio 13', 'La responsabilità di rispettare i diritti umani richiede che le imprese: a) evitino di causare o contribuire con le proprie attività agli impatti negativi sui diritti umani, e prendano le misure opportune quando tali impatti si verificano;  
b) si impegnino per prevenire o mitigare gli impatti negativi sui diritti umani direttamente collegati alle loro operazioni, ai loro prodotti o ai loro servizi in virtù di una relazione commerciale, anche se esse non abbiano contribuito a tali impatti.', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'Causa, contributo e collegamento diretto', 'UNGPs - Principio 13', 'UNGPs P.13', 'La responsabilità di rispettare i diritti umani richiede che le imprese: a) evitino di causare o contribuire con le proprie attività agli impatti negativi sui diritti umani, e prendano le misure opportune quando tali impatti si verificano; 
b) si impegnino per prevenire o mitigare gli impatti negativi sui diritti umani direttamente collegati alle loro operazioni, ai loro prodotti o ai loro servizi in virtù di una relazione commerciale, anche se esse non abbiano contribuito a tali impatti.');

-- Record 1765/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (597, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 14: Dimensione, settore, contesto operativo, assetto proprietario e struttura dell’impresa', 'UNGPs', 'Principio 14', 'La responsabilità delle imprese di rispettare i diritti umani si applica a tutte le imprese indipendentemente dalla loro dimensione, dal settore, dal contesto operativo, dell’assetto proprietario e dalla loro struttura. Nondimeno, la portata e la complessità degli strumenti con cui le imprese assolvono tale responsabilità possono variare in base ai summenzionati fattori e al livello di gravità degli impatti negativi dell’impresa sui diritti umani.', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'Dimensione, settore, contesto operativo, assetto proprietario e struttura dell’impresa', 'UNGPs - Principio 14', 'UNGPs P.14', 'La responsabilità delle imprese di rispettare i diritti umani si applica a tutte le imprese indipendentemente dalla loro dimensione, dal settore, dal contesto operativo, dell’assetto proprietario e dalla loro struttura. Nondimeno, la portata e la complessità degli strumenti con cui le imprese assolvono tale responsabilità possono variare in base ai summenzionati fattori e al livello di gravità degli impatti negativi dell’impresa sui diritti umani.');

-- Record 1766/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (597, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 16: La dichiarazione di intenti sull’impegno dell’impresa', 'UNGPs', 'Principio 16', 'Come base per interiorizzare la propria responsabilità di rispettare i diritti umani, le imprese dovrebbero esprimere il proprio impegno ad adempiere tale responsabilità attraverso una dichiarazione di intenti che:
 
 a) sia approvata dai vertici dell’impresa;
 b) sia stabilita attraverso la consulenza di esperti in materia interni e/o esterni; 
 c) definisca ciò che l’impresa si attende da dipendenti, partner commerciali e dalle altre parti direttamente collegate alle proprie attività, ai propri prodotti o ai propri servizi in materia di diritti umani; d) sia di pubblico dominio e divulgata all’interno o all’esterno a tutti i dipendenti, a tutti i partner commerciali e a tutte le altre parti interessate; 
 e) sia rispecchiata nelle politiche e nelle procedure operative necessarie per la sua integrazione in tutta l’impresa.', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'La dichiarazione di intenti sull’impegno dell’impresa', 'UNGPs - Principio 16', 'UNGPs P.16', 'Come base per interiorizzare la propria responsabilità di rispettare i diritti umani, le imprese dovrebbero esprimere il proprio impegno ad adempiere tale responsabilità attraverso una dichiarazione di intenti che:
a) sia approvata dai vertici dell’impresa;
b) sia stabilita attraverso la consulenza di esperti in materia interni e/o esterni; 
c) definisca ciò che l’impresa si attende da dipendenti, partner commerciali e dalle altre parti direttamente collegate alle proprie attività, ai propri prodotti o ai propri servizi in materia di diritti umani; d) sia di pubblico dominio e divulgata all’interno o all’esterno a tutti i dipendenti, a tutti i partner commerciali e a tutte le altre parti interessate; 
e) sia rispecchiata nelle politiche e nelle procedure operative necessarie per la sua integrazione in tutta l’impresa.');

-- Record 1767/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (597, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 17: La due diligence in materia di diritti umani', 'UNGPs', 'Principio 17', 'Il principio 17  stabilisce che: "Per poter identificare, prevenire e mitigare i propri impatti negativi sui diritti umani e per rendere conto del modo in cui siffatti impatti vengono affrontati, le imprese dovrebbero eseguire la due diligence sui diritti umani. Tale processo dovrebbe includere la valutazione dell’impatto effettivo e di quello potenziale sui diritti umani, l’integrazione dei risultati di tale valutazione, le misure da adottare in conseguenza, il monitoraggio delle risposte, e la comunicazione circa le modalità con cui siffatti impatti sono stati affrontati".', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'La due diligence in materia di diritti umani', 'UNGPs - Principio 17', 'UNGPs P.17', 'Il principio 17 stabilisce che: "Per poter identificare, prevenire e mitigare i propri impatti negativi sui diritti umani e per rendere conto del modo in cui siffatti impatti vengono affrontati, le imprese dovrebbero eseguire la due diligence sui diritti umani. Tale processo dovrebbe includere la valutazione dell’impatto effettivo e di quello potenziale sui diritti umani, l’integrazione dei risultati di tale valutazione, le misure da adottare in conseguenza, il monitoraggio delle risposte, e la comunicazione circa le modalità con cui siffatti impatti sono stati affrontati".');

-- Record 1768/2644
INSERT INTO esg_impact.form_legal_bases
