INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (605, '2000/532/CE: Decisione della Commissione, del 3 maggio 2000, che sostituisce la decisione 94/3/CE che istituisce un elenco di rifiuti conformemente all''articolo 1, lettera a), della direttiva 75/442/CEE del Consiglio relativa ai rifiuti e la decisione 94/904/CE del Consiglio che istituisce un elenco di rifiuti pericolosi ai sensi dell''articolo 1, paragrafo 4, della direttiva 91/689/CEE', 'Decisione della Commissione UE', '2000/532/CE', 'La Decisione 2000/532/CE istituisce un elenco standardizzato dei rifiuti, noto come Catalogo Europeo dei Rifiuti (CER), che facilita la classificazione, la gestione e il trattamento uniformi dei rifiuti in tutta l''Unione Europea, con particolare attenzione ai rifiuti pericolosi.', 'La decisione è un atto giuridico vincolante in tutti i suoi elementi. Le decisioni dell''Unione europea sono atti non legislativi quando non vengono adottate in conformità della la procedura legislativa. Possono essere adottate, ad esempio, dal Consiglio europeo, dal Consiglio o dalla Commissione europea.', 'Decisione della Commissione che sostituisce la decisione 94/3/CE', 'UE- 2000/532/CE', 'UE - 2000/532/CE', NULL);

-- Record 1847/2644
INSERT INTO esg_impact.form_legal_bases
