INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (605, 'WEF- Metriche del Capitalismo degli Stakeholder 2020 P2-6 (Rifiuti solidi)', 'WEF', '4P: Metrica P2-6', 'La tematica dei rifiuti solidi, parte del pilastro Pianeta, evidenzia l''impatto dei rifiuti non biodegradabili, in particolare della plastica monouso. Questi materiali, difficili da riciclare, spesso finiscono negli oceani, causando danni ecologici significativi e impiegando secoli per degradarsi.', 'Il World Economic Forum è l''Organizzazione Internazionale per la Cooperazione Pubblico-Privata. L''WEF insieme ad altre istituzioni ha sviluppato un insieme di metriche comuni per la rendicontazione della creazione di valore sostenibile (21 metriche principali e 34 metriche e divulgazioni ampliate) raggruprate in 4 pilastri principali (Principle of Governance, Planet, Persone and Prosperity). Per maggiore informazioni sulle 4 Pilastri (Metriche) di sostenibilità del WEF andare su: https://www.weforum.org/stakeholdercapitalism/our-metrics/', '6- Rifiuti solidi', 'WEF (MSC2024) 4P- P2-6', 'WEF (MSC2024) 4P- P2-6 EM 2', 'Impatto dello smaltimento dei rifiuti solidi');

-- Record 1861/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (606, 'SDGs: Goal 12 Consumo e produzione responsabili', 'SDGs', 'Goal 12', 'Garantire modelli sostenibili di produzione e di consumo', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Consumo e produzione responsabili', 'SDGs - Goal 12', 'SDG 12.5', 'Entro il 2030, ridurre in modo sostanziale la produzione di rifiuti attraverso la prevenzione, la riduzione, il riciclo e il riutilizzo');

-- Record 1862/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (606, '2000/532/CE: Decisione della Commissione, del 3 maggio 2000, che sostituisce la decisione 94/3/CE che istituisce un elenco di rifiuti conformemente all''articolo 1, lettera a), della direttiva 75/442/CEE del Consiglio relativa ai rifiuti e la decisione 94/904/CE del Consiglio che istituisce un elenco di rifiuti pericolosi ai sensi dell''articolo 1, paragrafo 4, della direttiva 91/689/CEE', 'Decisione della Commissione UE', '2000/532/CE', 'La Decisione 2000/532/CE istituisce un elenco standardizzato dei rifiuti, noto come Catalogo Europeo dei Rifiuti (CER), che facilita la classificazione, la gestione e il trattamento uniformi dei rifiuti in tutta l''Unione Europea, con particolare attenzione ai rifiuti pericolosi.', 'La decisione è un atto giuridico vincolante in tutti i suoi elementi. Le decisioni dell''Unione europea sono atti non legislativi quando non vengono adottate in conformità della la procedura legislativa. Possono essere adottate, ad esempio, dal Consiglio europeo, dal Consiglio o dalla Commissione europea.', 'Decisione della Commissione che sostituisce la decisione 94/3/CE', 'UE- 2000/532/CE', 'UE - 2000/532/CE', NULL);

-- Record 1863/2644
INSERT INTO esg_impact.form_legal_bases
