INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (606, 'Direttiva 2008/98/CE del Parlamento europeo e del Consiglio, del 19 novembre 2008, relativa ai rifiuti e che abroga alcune direttive', 'Direttiva (UE)', '2008/98/CE', 'Questa direttiva stabilisce misure volte a proteggere l’ambiente e la salute umana evitando o riducendo la produzione di rifiuti, gli effetti negativi della produzione e della gestione dei rifiuti, riducendo gli effetti generali dell’uso delle risorse e migliorandone l’efficienza, che costitui scono elementi fondamentali per il passaggio a un’economia circolare e per assicurare la competitività a lungo termine dell’Unione.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Relativa ai rifiuti', 'Direttiva (UE) - 2008/98/CE', 'Direttiva (UE) 2008/98/CE', NULL);

-- Record 1871/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (606, 'ESRS E5- Uso delle Risorse ed Economia circolare', 'ESRS', 'E5', 'Questo Standard stabilisce i requisiti di Informativa relativi all''"uso delle risorse" e all''"economia circolare" e in particolare su:
 (a) afflussi di risorse, compresa la circolarità degli afflussi di risorse materiali, considerando le risorse rinnovabili e non rinnovabili; e
 (b) deflussi di risorse, incluse le informazioni su prodotti e materiali; e
 (c) rifiuti.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Uso delle Risorse ed Economia circolare', 'ESRS - E5', 'ESRS (E5) E5-5 36 c AR 27', 'Tassi di contenuto riciclabile nei prodotti/
Tassi di contenuto riciclabile negli imballaggi dei prodotti');

-- Record 1872/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (606, 'Green Deal Europeo (EGD)', 'Strategia (UE)', 'Green Deal Europeo', 'Il Green Deal europeo è un pacchetto di iniziative strategiche che mira ad avviare l''UE sulla strada di una transizione verde, con l''obiettivo ultimo di raggiungere la neutralità climatica entro il 2050.', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Strategia dell''UE per conseguire l''obiettivo della neutralità climatica entro il 2050', 'Strategia (UE) - Green Deal Europeo', 'Green Deal Europeo', 'Green Deal Europeo: Strategia dell''UE per conseguire l''obiettivo della neutralità climatica entro il 2050');

