INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (607, 'ESRS E5- Uso delle Risorse ed Economia circolare', 'ESRS', 'E5', 'Questo Standard stabilisce i requisiti di Informativa relativi all''"uso delle risorse" e all''"economia circolare" e in particolare su:
 (a) afflussi di risorse, compresa la circolarità degli afflussi di risorse materiali, considerando le risorse rinnovabili e non rinnovabili; e
 (b) deflussi di risorse, incluse le informazioni su prodotti e materiali; e
 (c) rifiuti.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Uso delle Risorse ed Economia circolare', 'ESRS - E5', 'ESRS (E5) E5-5 36 c AR 27', 'Tassi di contenuto riciclabile nei prodotti/
Tassi di contenuto riciclabile negli imballaggi dei prodotti');

-- Record 1887/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (607, 'Green Deal Europeo (EGD)', 'Strategia (UE)', 'Green Deal Europeo', 'Il Green Deal europeo è un pacchetto di iniziative strategiche che mira ad avviare l''UE sulla strada di una transizione verde, con l''obiettivo ultimo di raggiungere la neutralità climatica entro il 2050.', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Strategia dell''UE per conseguire l''obiettivo della neutralità climatica entro il 2050', 'Strategia (UE) - Green Deal Europeo', 'Green Deal Europeo', 'Green Deal Europeo: Strategia dell''UE per conseguire l''obiettivo della neutralità climatica entro il 2050');

