INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (608, 'Piano d''azione per l''economia circolare 2020 (CEAP 2020 - EU)', 'Piano Azione (UE)', 'Economia Circolare', 'Questo piano d''azione per l''economia circolare stabilisce un programma orientato al futuro per costruire un''Europa più pulita e competitiva in co-creazione con gli operatori economici, i consumatori, i cittadini e le organizzazioni della società civile. Mira ad accelerare il profondo cambiamento richiesto dal Green Deal europeo, sulla base delle azioni in materia di economia circolare attuate dal sin dal 2015.', 'Un piano di azione a livello dell''Unione Europea (UE) è un documento strategico che definisce obiettivi specifici e misure concrete per affrontare sfide o perseguire opportunità a livello comunitario. Questi piani sono sviluppati e implementati dall''UE per guidare l''azione politica e normativa su questioni di interesse comune tra gli Stati membri.', 'Piano per l''Economia circolare 2020', 'Piano Azione (UE) - Economia Circolare', 'Piano Azione (UE) Economia Circolare', 'Piano d''azione per l''economia circolare 2020');

