INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (608, 'Strategia Nazionale per l''Economia Circolare', 'Strategia Nazionale (IT)', 'Economia Circolare', 'La Strategia Nazionale per l’Economia Circolare è un piano elaborato dall''Italia per promuovere un modello economico sostenibile, in cui le risorse vengono utilizzate in modo efficiente e i rifiuti vengono ridotti al minimo.', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia per l''Economia Circolare', 'Strategia (IT) Economia Circolare', 'Strategia (IT) Economia Circolare', 'Strategia Nazionale per l''Economia Circolare');

