INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (611, 'ISO 14064: Emissioni Gas Serra', 'ISO', '14064', 'L''ISO 14064 è una serie di standard internazionali che forniscono linee guida e requisiti per la quantificazione, il monitoraggio e la comunicazione delle emissioni di gas serra (GHG, Greenhouse Gas).', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Emissioni Gas Serra', 'ISO - 14064', 'ISO 14064', 'Emissioni Gas Serra');

