INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (611, 'Piano Nazionale Adattamento Cambiamenti Climatici (PNACC)', 'Piano Nazionale (IT)', 'Adattamento Cambiamento Climatico', 'La finalità del Piano è contenere la vulnerabilità dei sistemi naturali, sociali ed economici agli impatti dei cambiamenti climatici e aumentarne la resilienza. Rappresenta lo strumento di attuazione della Strategia Nazionale di adattamento ai cambiamenti climatici del 2015 per la pianificazione nazionale a supporto delle istituzioni che saranno chiamate a sviluppare sulla propria scala di governo i contenuti del piano, tenendo conto delle specificità dei diversi contesti.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Cambiamento Climatico (PNACC)', 'Piano Nazionale (IT) Adattamento Cambiamento Climatico', 'PNACC', 'Piano Nazionale Adattamento Cambiamenti Climatici');

-- Record 1945/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (611, 'Protocollo di Kyoto della Convenzione Quadro delle Nazioni Unite sui Cambiamenti Climatici', 'Protocollo (Int.)', 'Cambiamenti Climatici (Protocollo Kyoto)', 'Il protocollo di Kyoto, che fa seguito alla convenzione quadro delle Nazioni Unite sui cambiamenti climatici, è uno dei più importanti strumenti giuridici internazionali volti a combattere i cambiamenti climatici. Esso contiene gli impegni dei paesi industrializzati a ridurre le emissioni di alcuni gas ad effetto serra, responsabili del riscaldamento del pianeta.', 'Un protocollo è un tipo di accordo formale tra Stati o altre entità sovrane che integra, modifica o specifica ulteriormente un trattato esistente. I protocolli sono strumenti giuridici che possono avere diverse funzioni e scopi, tra cui l''espansione di disposizioni di un trattato, la creazione di nuovi obblighi o la regolamentazione di questioni specifiche non completamente coperte dal trattato originale.', 'Quadro delle Nazioni Unite sui Cambiamenti Climatici', 'Protocollo - Cambiamenti Climatici (Protocollo Kyoto)', 'Protocollo Kyoto', 'Convenzione Quadro delle Nazioni Unite sui Cambiamenti Climatici');

-- Record 1946/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (611, 'Protocollo di Montreal relativo a sostanze che riducono lo strato di ozono', 'Protocollo (Int.)', 'Sostanze che riducono lo strato di ozono (Protocollo Montreal)', 'Il protocollo di Montreal (alla convenzione di Vienna per la protezione dello strato di ozono) è un accordo globale volto a proteggere lo strato di ozono stratosferico terrestre eliminando gradualmente le sostanze chimiche che lo riducono. Tale progressiva riduzione riguarda sia la produzione che il consumo di sostanze che riducono lo strato di ozono (ODS). Il suo emendamento più recente, l’emendamento Kigali, richiede la graduale riduzione graduale degli HFC.', 'Un protocollo è un tipo di accordo formale tra Stati o altre entità sovrane che integra, modifica o specifica ulteriormente un trattato esistente. I protocolli sono strumenti giuridici che possono avere diverse funzioni e scopi, tra cui l''espansione di disposizioni di un trattato, la creazione di nuovi obblighi o la regolamentazione di questioni specifiche non completamente coperte dal trattato originale.', 'Protocollo relativo a sostanze che riducono lo strato di ozono', 'Protocollo - Sostanze che riducono lo strato di ozono (Protocollo Montreal)', 'Protocollo Montreal', 'Protocollo relativo a sostanze che riducono lo strato di ozono');

-- Record 1947/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (611, 'Protocollo sui Gas a Effetto Serra (GHG Protocol)', 'Protocollo (Int.)', 'Gas a Effetto Serra', 'Il Protocollo sui Gas a Effetto Serra (Greenhouse Gas Protocol, GHG Protocol) è uno standard internazionale utilizzato per misurare e gestire le emissioni di gas a effetto serra (GHG) da parte delle organizzazioni. È stato sviluppato da una partnership tra il World Resources Institute (WRI) e il World Business Council for Sustainable Development (WBCSD), ed è considerato uno dei principali riferimenti per la rendicontazione delle emissioni di carbonio a livello globale.', 'Un protocollo è un tipo di accordo formale tra Stati o altre entità sovrane che integra, modifica o specifica ulteriormente un trattato esistente. I protocolli sono strumenti giuridici che possono avere diverse funzioni e scopi, tra cui l''espansione di disposizioni di un trattato, la creazione di nuovi obblighi o la regolamentazione di questioni specifiche non completamente coperte dal trattato originale.', 'GHG Protocol', 'Protocollo - sui Gas a Effetto Serra', 'Protocollo GHG', 'Protocollo sui Gas a Effetto Serra');

-- Record 1948/2644
INSERT INTO esg_impact.form_legal_bases
