INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (613, 'GRI 201- Performance economica (2016)', 'GRI', '201', 'Lo Standard 201 tratta il tema della performance economica, che include il valore economico generato e distribuito (EVG&D) da un’organizzazione, gli obblighi relative al piano di benefit definiti, l’assistenza finanziaria che riceve da qualsiasi governo e le implicazioni finanziarie del cambiamento climatico.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Performance economica (2016)', 'GRI - 201 (2016)', 'GRI 201-2 (2016)', 'Implicazioni finanziarie e altri rischi e opportunità dovuti al cambiamento climatico');

-- Record 1984/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (613, 'IFRS S2 - Climate-related Disclosures', 'IFRS', 'S2', 'L''obiettivo dello IFRS S2 Climate-related Disclosures è richiedere alle entità di divulgare informazioni sui rischi e le opportunità legati al clima, utili agli utenti principali dei report finanziari per prendere decisioni sulla fornitura di risorse.', 'L''IFRS S2 Climate-related Disclosures richiede che un''entità fornisca informazioni sui propri rischi e opportunità legati al clima affinché tali informazioni siano utili ai principali utilizzatori dei bilanci finanziari generali, consentendo loro di prendere decisioni informate sulla fornitura di risorse all''entità.', 'Divulgazioni relative al clima', 'IFRS- S2', 'IFRS S2.10', 'Rischi e opportunità legati al clima.');

-- Record 1985/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (613, 'Piano Nazionale Adattamento Cambiamenti Climatici (PNACC)', 'Piano Nazionale (IT)', 'Adattamento Cambiamento Climatico', 'La finalità del Piano è contenere la vulnerabilità dei sistemi naturali, sociali ed economici agli impatti dei cambiamenti climatici e aumentarne la resilienza. Rappresenta lo strumento di attuazione della Strategia Nazionale di adattamento ai cambiamenti climatici del 2015 per la pianificazione nazionale a supporto delle istituzioni che saranno chiamate a sviluppare sulla propria scala di governo i contenuti del piano, tenendo conto delle specificità dei diversi contesti.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Cambiamento Climatico (PNACC)', 'Piano Nazionale (IT) Adattamento Cambiamento Climatico', 'PNACC', 'Piano Nazionale Adattamento Cambiamenti Climatici');

-- Record 1986/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (613, 'Piano Nazionale di contabilizzazione forestale dell’Italia (2021-2025)', 'Piano Nazionale (IT)', 'Contabilizzazione forestale dell’Italia', 'Il Piano Nazionale di Contabilizzazione Forestale dell''Italia (PNCF) è un documento strategico che descrive come il paese intende monitorare, rendicontare e gestire il carbonio forestale, in conformità con gli obblighi internazionali e le politiche nazionali di mitigazione dei cambiamenti climatici. Questo piano è una parte essenziale delle politiche ambientali e climatiche italiane, poiché le foreste giocano un ruolo cruciale nel sequestro del carbonio e nella riduzione delle emissioni di gas serra.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Piano di contabilizzazione forestale dell’Italia (2021-2025)', 'Piano Nazionale (IT) - Contabilizzazione forestale dell’Italia', 'PNCFI (NFAP)', 'Piano Nazionale di contabilizzazione forestale dell’Italia');

-- Record 1987/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (613, 'Quadro per le politiche dell''energia e del clima 2030 (UE)', 'Strategia (UE)', 'Politiche dell''energia e del clima 2030', 'Il Quadro europeo per il clima e l''energia al 2030 è una strategia dell''Unione Europea (UE) che stabilisce obiettivi ambiziosi per ridurre le emissioni di gas serra e promuovere fonti energetiche rinnovabili entro il 2030', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Quadro per le politiche dell''energia e del clima 2030 dell''UE', 'Strategia (UE) - Politiche dell''energia e del clima 2030', 'Politiche (UE) dell''energia e del clima 2030', 'Quadro per le politiche dell''energia e del clima 2030 dell''UE');

-- Record 1988/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (613, 'Raccomandazioni della Task Force sulle Informazioni Finanziarie relative al Clima (TCFD) - Rischi, opportunità e impatti finanziari legati al clima', 'TCFD', 'Rischi, opportunità e impatti finanziari legati al clima', 'Rischi, Opportunità e Impatti Finanziari legati al Clima identifica la crescente richiesta di informazioni finanziarie utili e orientate al clima da parte di investitori, finanziatori, assicuratori e altri stakeholder. La Task Force sottolinea l''importanza di un quadro standardizzato per allineare le diverse normative e favorire la coerenza nelle informazioni a livello internazionale.', 'La Task Force sulle Informazioni Finanziarie relative al Clima (Task Force on Climate-related Financial Disclosures, TCFD)  ha il compito di promuovere la trasparenza nelle informazioni finanziarie legate al clima, rendendo i dati più comparabili, coerenti e utili per investitori, creditori, assicuratori e altre parti interessate.', 'Clima', 'TCFD - Rischi, opportunità e impatti finanziari legati al clima', 'TCFD - Clima', 'Rischi, opportunità e impatti finanziari legati al clima');

-- Record 1989/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (613, 'Regolamento (UE) 2021/1119 del Parlamento europeo e del Consiglio del 30 giugno 2021 che istituisce il quadro per il conseguimento della neutralità climatica e che modifica il regolamento (CE) n. 401/2009 e il regolamento (UE) 2018/1999 («European Climate Law»)', 'Regolamento (UE)', '2021/1119', 'Quadro per il conseguimento della neutralità climatica e che modifica il regolamento (CE) n. 401/2009 e il regolamento (UE) 2018/1999 («European Climate Law») . 
 
 La Legge Europea sul Clima trasforma in legge l''obiettivo stabilito nel Green Deal Europeo per rendere l''economia e la società europee neutrali dal punto di vista climatico entro il 2050. La legge stabilisce anche l''obiettivo intermedio di ridurre le emissioni nette di gas serra del 55% entro il 2030, rispetto ai livelli del 1990.', 'I regolamenti sono atti giuridici definiti nell’articolo 288 del trattato sul funzionamento dell’Unione europea (TFUE). Hanno portata generale, sono vincolanti in tutti i loro elementi e direttamente applicabili negli Stati membri dell’Unione. I regolamenti rientrano nel diritto derivato dell’Unione. Sono adottati dalle istituzioni dell’Unione europea sulla base dei trattati. Un regolamento ha carattere vincolante in tutti i suoi elementi, ha portata generale ed è rivolto a categorie astratte di persone, non a specifici individui. 
 https://eur-lex.europa.eu/IT/legal-content/summary/european-union-regulations.html', 'Regolamento che istituisce il quadro per il conseguimento della neutralità climatica e che modifica il regolamento (CE) n. 401/2009 e il regolamento (UE) 2018/1999 (Legge europea sul clima)', 'Regolamento (UE) - 2021/1119', 'Regolamento (UE) 2021/1119', NULL);

