INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (614, 'GRI 201- Performance economica (2016)', 'GRI', '201', 'Lo Standard 201 tratta il tema della performance economica, che include il valore economico generato e distribuito (EVG&D) da un’organizzazione, gli obblighi relative al piano di benefit definiti, l’assistenza finanziaria che riceve da qualsiasi governo e le implicazioni finanziarie del cambiamento climatico.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Performance economica (2016)', 'GRI - 201 (2016)', 'GRI 201-2 (2016)', 'Implicazioni finanziarie e altri rischi e opportunità dovuti al cambiamento climatico');

-- Record 2001/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (614, 'IFRS S2 - Climate-related Disclosures', 'IFRS', 'S2', 'L''obiettivo dello IFRS S2 Climate-related Disclosures è richiedere alle entità di divulgare informazioni sui rischi e le opportunità legati al clima, utili agli utenti principali dei report finanziari per prendere decisioni sulla fornitura di risorse.', 'L''IFRS S2 Climate-related Disclosures richiede che un''entità fornisca informazioni sui propri rischi e opportunità legati al clima affinché tali informazioni siano utili ai principali utilizzatori dei bilanci finanziari generali, consentendo loro di prendere decisioni informate sulla fornitura di risorse all''entità.', 'Divulgazioni relative al clima', 'IFRS- S2', 'IFRS S2.10', 'Rischi e opportunità legati al clima.');

-- Record 2002/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (614, 'Piano Nazionale Adattamento Cambiamenti Climatici (PNACC)', 'Piano Nazionale (IT)', 'Adattamento Cambiamento Climatico', 'La finalità del Piano è contenere la vulnerabilità dei sistemi naturali, sociali ed economici agli impatti dei cambiamenti climatici e aumentarne la resilienza. Rappresenta lo strumento di attuazione della Strategia Nazionale di adattamento ai cambiamenti climatici del 2015 per la pianificazione nazionale a supporto delle istituzioni che saranno chiamate a sviluppare sulla propria scala di governo i contenuti del piano, tenendo conto delle specificità dei diversi contesti.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Cambiamento Climatico (PNACC)', 'Piano Nazionale (IT) Adattamento Cambiamento Climatico', 'PNACC', 'Piano Nazionale Adattamento Cambiamenti Climatici');

-- Record 2003/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (614, 'Piano Nazionale di contabilizzazione forestale dell’Italia (2021-2025)', 'Piano Nazionale (IT)', 'Contabilizzazione forestale dell’Italia', 'Il Piano Nazionale di Contabilizzazione Forestale dell''Italia (PNCF) è un documento strategico che descrive come il paese intende monitorare, rendicontare e gestire il carbonio forestale, in conformità con gli obblighi internazionali e le politiche nazionali di mitigazione dei cambiamenti climatici. Questo piano è una parte essenziale delle politiche ambientali e climatiche italiane, poiché le foreste giocano un ruolo cruciale nel sequestro del carbonio e nella riduzione delle emissioni di gas serra.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Piano di contabilizzazione forestale dell’Italia (2021-2025)', 'Piano Nazionale (IT) - Contabilizzazione forestale dell’Italia', 'PNCFI (NFAP)', 'Piano Nazionale di contabilizzazione forestale dell’Italia');

-- Record 2004/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (614, 'Quadro per le politiche dell''energia e del clima 2030 (UE)', 'Strategia (UE)', 'Politiche dell''energia e del clima 2030', 'Il Quadro europeo per il clima e l''energia al 2030 è una strategia dell''Unione Europea (UE) che stabilisce obiettivi ambiziosi per ridurre le emissioni di gas serra e promuovere fonti energetiche rinnovabili entro il 2030', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Quadro per le politiche dell''energia e del clima 2030 dell''UE', 'Strategia (UE) - Politiche dell''energia e del clima 2030', 'Politiche (UE) dell''energia e del clima 2030', 'Quadro per le politiche dell''energia e del clima 2030 dell''UE');

-- Record 2005/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (614, 'Raccomandazioni della Task Force sulle Informazioni Finanziarie relative al Clima (TCFD) - Rischi, opportunità e impatti finanziari legati al clima', 'TCFD', 'Rischi, opportunità e impatti finanziari legati al clima', 'Rischi, Opportunità e Impatti Finanziari legati al Clima identifica la crescente richiesta di informazioni finanziarie utili e orientate al clima da parte di investitori, finanziatori, assicuratori e altri stakeholder. La Task Force sottolinea l''importanza di un quadro standardizzato per allineare le diverse normative e favorire la coerenza nelle informazioni a livello internazionale.', 'La Task Force sulle Informazioni Finanziarie relative al Clima (Task Force on Climate-related Financial Disclosures, TCFD)  ha il compito di promuovere la trasparenza nelle informazioni finanziarie legate al clima, rendendo i dati più comparabili, coerenti e utili per investitori, creditori, assicuratori e altre parti interessate.', 'Clima', 'TCFD - Rischi, opportunità e impatti finanziari legati al clima', 'TCFD - Clima', 'Rischi, opportunità e impatti finanziari legati al clima');

-- Record 2006/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (614, 'Regolamento (UE) 2021/1119 del Parlamento europeo e del Consiglio del 30 giugno 2021 che istituisce il quadro per il conseguimento della neutralità climatica e che modifica il regolamento (CE) n. 401/2009 e il regolamento (UE) 2018/1999 («European Climate Law»)', 'Regolamento (UE)', '2021/1119', 'Quadro per il conseguimento della neutralità climatica e che modifica il regolamento (CE) n. 401/2009 e il regolamento (UE) 2018/1999 («European Climate Law») . 
 
 La Legge Europea sul Clima trasforma in legge l''obiettivo stabilito nel Green Deal Europeo per rendere l''economia e la società europee neutrali dal punto di vista climatico entro il 2050. La legge stabilisce anche l''obiettivo intermedio di ridurre le emissioni nette di gas serra del 55% entro il 2030, rispetto ai livelli del 1990.', 'I regolamenti sono atti giuridici definiti nell’articolo 288 del trattato sul funzionamento dell’Unione europea (TFUE). Hanno portata generale, sono vincolanti in tutti i loro elementi e direttamente applicabili negli Stati membri dell’Unione. I regolamenti rientrano nel diritto derivato dell’Unione. Sono adottati dalle istituzioni dell’Unione europea sulla base dei trattati. Un regolamento ha carattere vincolante in tutti i suoi elementi, ha portata generale ed è rivolto a categorie astratte di persone, non a specifici individui. 
 https://eur-lex.europa.eu/IT/legal-content/summary/european-union-regulations.html', 'Regolamento che istituisce il quadro per il conseguimento della neutralità climatica e che modifica il regolamento (CE) n. 401/2009 e il regolamento (UE) 2018/1999 (Legge europea sul clima)', 'Regolamento (UE) - 2021/1119', 'Regolamento (UE) 2021/1119', NULL);

-- Record 2007/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (614, 'Regolamento Delegato (UE) 2020/1818 della Commissione del 17 luglio 2020 che integra il regolamento (UE) 2016/1011 del Parlamento europeo e del Consiglio per quanto riguarda le norme minime per gli indici di riferimento UE di transizione climatica e per gli indici di riferimento UE allineati con l’accordo di Parigi', 'Regolamento (UE)', '2020/1818', 'Questo regolamento integra le norme minime per gli indici di riferimento UE di transizione climatica e per gli indici di riferimento UE allineati con l’accordo di Parigi.', 'I regolamenti sono atti giuridici definiti nell’articolo 288 del trattato sul funzionamento dell’Unione europea (TFUE). Hanno portata generale, sono vincolanti in tutti i loro elementi e direttamente applicabili negli Stati membri dell’Unione. I regolamenti rientrano nel diritto derivato dell’Unione. Sono adottati dalle istituzioni dell’Unione europea sulla base dei trattati. Un regolamento ha carattere vincolante in tutti i suoi elementi, ha portata generale ed è rivolto a categorie astratte di persone, non a specifici individui. 
 https://eur-lex.europa.eu/IT/legal-content/summary/european-union-regulations.html', 'Regolamento che integra le norme minime per gli indici di riferimento UE di transizione climatica', 'Regolamento (UE) - 2020/1818', 'Regolamento (UE) 2020/1818', NULL);

-- Record 2008/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (614, 'Strategia Nazionale per lo Sviluppo Sostenibile (SNSvS)', 'Strategia Nazionale (IT)', 'SNSvS', 'In Italia rappresenta il quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali, declinando per il contesto nazionale gli Obiettivi di Sviluppo Sostenibile (SDG) delineati dall''ONU', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia Nazionale italiana per lo Sviluppo Sostenibile', 'SNSvS (2022)', 'SNSvS22- Pianeta III.1', 'Promuovere il presidio e la manutenzione del territorio e rafforzare le capacità di resilienza di comunità e territori anche in riferimento agli impatti dei cambiamenti climatici');

-- Record 2009/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'SDGs: Goal 13 Lotta contro il cambiamento climatico', 'SDGs', 'Goal 13', 'Promuovere azioni, a tutti i livelli, per combattere il cambiamento climatico', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Lotta contro il cambiamento climatico', 'SDGs - Goal 13', 'SDG 13.1', 'Rafforzare in tutti i paesi la capacità di ripresa e di adattamento ai rischi legati al clima e ai disastri naturali');

-- Record 2010/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'Accordo di Parigi sui cambiamenti climatici', 'Trattato internazionale', 'Accordo di Parigi sui Cambiamenti climatici', 'L''accordo di Parigi è un trattato internazionale che presenta un piano d''azione per limitare il riscaldamento globale. L’accordo di Parigi, firmato da 194 paesi e dall’UE, mira a limitare il riscaldamento globale al di sotto di 2°C e a proseguire gli sforzi per circoscriverlo a 1,5°C al fine di evitare le conseguenze catastrofiche del cambiamento climatico.', 'L''accordo internazionale può essere considerato come un contratto stipulato tra due o più Stati, diretto a regolare una determinata sfera di rapporti tra i contraenti. La sua formazione è regolata da una serie di norme consuetudinarie e convenzionali che rientrano nel diritto pubblico internazionale.', 'Accordo sui cambiamenti climatici', 'Trattato internazionale - Accordo di Parigi sui Cambiamenti climatici', 'Accordo Parigi (2015)', 'Accordo che mira a limitare il riscaldamento globale al di sotto di 2°C');

-- Record 2011/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'Decreto Legislativo 6 settembre 2024, n. 125 Attuazione della direttiva 2022/2464/UE del Parlamento europeo e del Consiglio del 14 dicembre 2022, recante modifica del regolamento 537/2014/UE, della direttiva 2004/109/CE, della direttiva 2006/43/CE e della direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Decreto (IT)', '125/2024', 'Il Decreto legislativo 6 settembre 2024, n. 125 attua la Direttiva 2022/2464/UE (nota come Corporate Sustainability Reporting Directive, CSRD). Questo decreto introduce nuovi obblighi di rendicontazione societaria di sostenibilità per le imprese, ampliando notevolmente le informazioni che devono essere incluse nei bilanci aziendali in materia di sostenibilità ambientale, sociale e di governance (ESG).', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Attuazione della direttiva 2022/2464/UE (CSRD)', 'Decreto Legislativo - 125/2024', 'D.Lgs. 125/2024', 'Attuazione della direttiva 2022/2464/UE (CSRD)');

-- Record 2012/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'Direttiva (UE) 2016/2284 concernente la riduzione delle emissioni nazionali di determinati inquinanti atmosferici, che modifica la direttiva 2003/35/CE e abroga la direttiva 2001/81/CE', 'Direttiva (UE)', '2016/2284', 'La direttiva stabilisce gli impegni di riduzione delle emissioni per le emissioni atmosferiche antropogeniche degli Stati membri di biossido di zolfo (SO2), ossidi di azoto (NOx), composti organici volatili non metanici (COVNM), ammoniaca (NH3), e particolato fine (PM2,5) e impone l''elaborazione, l''adozione e l''attuazione di programmi nazionali di controllo dell''inquinamento atmosferico e il monitoraggio e la comunicazione in merito ai suddetti inquinanti e agli altri inquinanti indicati all''allegato I e ai loro effetti.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva concernente alla riduzione delle emissioni nazionali di determinati inquinanti atmosferici', 'Direttiva (UE) - 2016/2284', 'Direttiva (UE) 2016/2284', NULL);

-- Record 2013/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'Direttiva (UE) 2022/2464 del Parlamento europeo e del Consiglio del 14 dicembre 2022 che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE)', '2022/2464', 'La CSRD o Corporate Sustainability Reporting Directive (Direttiva 2022/2464) è la direttiva europea che mira a migliorare la trasparenza e la qualità delle informazioni sulla sostenibilità fornite dalle aziende. La CSRD sostituisce e amplia la Non-Financial Reporting Directive (NFRD), aumentando le esigenze di rendicontazione per le imprese riguardo alle loro attività sostenibili. I punti chiavi della CSRD sono: -L''ampiezza del campo di applicazione rispetto alla NFRD rispetto alle aziende che devono rendicontare, - le informazioni da rendicontare in termini ESG e di sostenibilità; - lo standard di rendicontazione (gli ESRS); - L''audit e certificazione; e la digitalizzazione delle informazioni. 
 
 L''obiettivo principale della CSRD è migliorare la trasparenza e responsabilizzare le aziende verso pratiche più sostenibili, aiutando investitori, consumatori, e altre parti interessate a prendere decisioni informate basate su dati di sostenibilità accurati e completi.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE) - 2022/2464', 'CSRD', 'Rendicontazione societaria di sostenibilità (CSRD)');

-- Record 2014/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'ESRS E1- Cambiamento climatico', 'ESRS', 'E1', 'Questo Standard copre i requisiti di Informativa GRI  relativi ai seguenti aspetti della sostenibilità: "Mitigazione del cambiamento climatico" e "Adattamento al cambiamento climatico". Copre anche le questioni energetiche, nella misura in cui sono rilevanti per il cambiamento climatico.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Cambiamento climatico', 'ESRS - E1', 'ESRS (E1) E1-3 28', 'Azioni e risorse relative alla mitigazione e all''adattamento ai cambiamenti climatici');

-- Record 2015/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'EU - Strategia a lungo termine per la neutralità climatica al 2050', 'Strategia (UE)', 'Neutralità climatica al 2050', 'Strategia con l''obiettivo di raggiungere un''Unione Europea climaticamente neutrale entro il 2050, in linea con gli obiettivi dell''Accordo di Parigi e alla luce delle più recenti conoscenze scientifiche disponibili e della necessità di intensificare l''azione climatica globale.', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Strategia a lungo termine per la neutralità climatica al 2050', 'Strategia (UE) - Neutralità climatica al 2050', 'Strategia (UE) Neutralità climatica al 2050', 'Strategia a lungo termine per la neutralità climatica al 2050');

-- Record 2016/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'Green Deal Europeo (EGD)', 'Strategia (UE)', 'Green Deal Europeo', 'Il Green Deal europeo è un pacchetto di iniziative strategiche che mira ad avviare l''UE sulla strada di una transizione verde, con l''obiettivo ultimo di raggiungere la neutralità climatica entro il 2050.', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Strategia dell''UE per conseguire l''obiettivo della neutralità climatica entro il 2050', 'Strategia (UE) - Green Deal Europeo', 'Green Deal Europeo', 'Green Deal Europeo: Strategia dell''UE per conseguire l''obiettivo della neutralità climatica entro il 2050');

-- Record 2017/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'GRI 201- Performance economica (2016)', 'GRI', '201', 'Lo Standard 201 tratta il tema della performance economica, che include il valore economico generato e distribuito (EVG&D) da un’organizzazione, gli obblighi relative al piano di benefit definiti, l’assistenza finanziaria che riceve da qualsiasi governo e le implicazioni finanziarie del cambiamento climatico.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Performance economica (2016)', 'GRI - 201 (2016)', 'GRI 201-2 (2016)', 'Implicazioni finanziarie e altri rischi e opportunità dovuti al cambiamento climatico');

-- Record 2018/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'IFRS S2 - Climate-related Disclosures', 'IFRS', 'S2', 'L''obiettivo dello IFRS S2 Climate-related Disclosures è richiedere alle entità di divulgare informazioni sui rischi e le opportunità legati al clima, utili agli utenti principali dei report finanziari per prendere decisioni sulla fornitura di risorse.', 'L''IFRS S2 Climate-related Disclosures richiede che un''entità fornisca informazioni sui propri rischi e opportunità legati al clima affinché tali informazioni siano utili ai principali utilizzatori dei bilanci finanziari generali, consentendo loro di prendere decisioni informate sulla fornitura di risorse all''entità.', 'Divulgazioni relative al clima', 'IFRS- S2', 'IFRS S2.10', 'Rischi e opportunità legati al clima.');

-- Record 2019/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'Piano Nazionale Adattamento Cambiamenti Climatici (PNACC)', 'Piano Nazionale (IT)', 'Adattamento Cambiamento Climatico', 'La finalità del Piano è contenere la vulnerabilità dei sistemi naturali, sociali ed economici agli impatti dei cambiamenti climatici e aumentarne la resilienza. Rappresenta lo strumento di attuazione della Strategia Nazionale di adattamento ai cambiamenti climatici del 2015 per la pianificazione nazionale a supporto delle istituzioni che saranno chiamate a sviluppare sulla propria scala di governo i contenuti del piano, tenendo conto delle specificità dei diversi contesti.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Cambiamento Climatico (PNACC)', 'Piano Nazionale (IT) Adattamento Cambiamento Climatico', 'PNACC', 'Piano Nazionale Adattamento Cambiamenti Climatici');

-- Record 2020/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'Piano Nazionale di contabilizzazione forestale dell’Italia (2021-2025)', 'Piano Nazionale (IT)', 'Contabilizzazione forestale dell’Italia', 'Il Piano Nazionale di Contabilizzazione Forestale dell''Italia (PNCF) è un documento strategico che descrive come il paese intende monitorare, rendicontare e gestire il carbonio forestale, in conformità con gli obblighi internazionali e le politiche nazionali di mitigazione dei cambiamenti climatici. Questo piano è una parte essenziale delle politiche ambientali e climatiche italiane, poiché le foreste giocano un ruolo cruciale nel sequestro del carbonio e nella riduzione delle emissioni di gas serra.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Piano di contabilizzazione forestale dell’Italia (2021-2025)', 'Piano Nazionale (IT) - Contabilizzazione forestale dell’Italia', 'PNCFI (NFAP)', 'Piano Nazionale di contabilizzazione forestale dell’Italia');

-- Record 2021/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'Quadro per le politiche dell''energia e del clima 2030 (UE)', 'Strategia (UE)', 'Politiche dell''energia e del clima 2030', 'Il Quadro europeo per il clima e l''energia al 2030 è una strategia dell''Unione Europea (UE) che stabilisce obiettivi ambiziosi per ridurre le emissioni di gas serra e promuovere fonti energetiche rinnovabili entro il 2030', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Quadro per le politiche dell''energia e del clima 2030 dell''UE', 'Strategia (UE) - Politiche dell''energia e del clima 2030', 'Politiche (UE) dell''energia e del clima 2030', 'Quadro per le politiche dell''energia e del clima 2030 dell''UE');

-- Record 2022/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'Raccomandazioni della Task Force sulle Informazioni Finanziarie relative al Clima (TCFD) - Rischi, opportunità e impatti finanziari legati al clima', 'TCFD', 'Rischi, opportunità e impatti finanziari legati al clima', 'Rischi, Opportunità e Impatti Finanziari legati al Clima identifica la crescente richiesta di informazioni finanziarie utili e orientate al clima da parte di investitori, finanziatori, assicuratori e altri stakeholder. La Task Force sottolinea l''importanza di un quadro standardizzato per allineare le diverse normative e favorire la coerenza nelle informazioni a livello internazionale.', 'La Task Force sulle Informazioni Finanziarie relative al Clima (Task Force on Climate-related Financial Disclosures, TCFD)  ha il compito di promuovere la trasparenza nelle informazioni finanziarie legate al clima, rendendo i dati più comparabili, coerenti e utili per investitori, creditori, assicuratori e altre parti interessate.', 'Clima', 'TCFD - Rischi, opportunità e impatti finanziari legati al clima', 'TCFD - Clima', 'Rischi, opportunità e impatti finanziari legati al clima');

-- Record 2023/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'Regolamento (UE) 2021/1119 del Parlamento europeo e del Consiglio del 30 giugno 2021 che istituisce il quadro per il conseguimento della neutralità climatica e che modifica il regolamento (CE) n. 401/2009 e il regolamento (UE) 2018/1999 («European Climate Law»)', 'Regolamento (UE)', '2021/1119', 'Quadro per il conseguimento della neutralità climatica e che modifica il regolamento (CE) n. 401/2009 e il regolamento (UE) 2018/1999 («European Climate Law») . 
 
 La Legge Europea sul Clima trasforma in legge l''obiettivo stabilito nel Green Deal Europeo per rendere l''economia e la società europee neutrali dal punto di vista climatico entro il 2050. La legge stabilisce anche l''obiettivo intermedio di ridurre le emissioni nette di gas serra del 55% entro il 2030, rispetto ai livelli del 1990.', 'I regolamenti sono atti giuridici definiti nell’articolo 288 del trattato sul funzionamento dell’Unione europea (TFUE). Hanno portata generale, sono vincolanti in tutti i loro elementi e direttamente applicabili negli Stati membri dell’Unione. I regolamenti rientrano nel diritto derivato dell’Unione. Sono adottati dalle istituzioni dell’Unione europea sulla base dei trattati. Un regolamento ha carattere vincolante in tutti i suoi elementi, ha portata generale ed è rivolto a categorie astratte di persone, non a specifici individui. 
 https://eur-lex.europa.eu/IT/legal-content/summary/european-union-regulations.html', 'Regolamento che istituisce il quadro per il conseguimento della neutralità climatica e che modifica il regolamento (CE) n. 401/2009 e il regolamento (UE) 2018/1999 (Legge europea sul clima)', 'Regolamento (UE) - 2021/1119', 'Regolamento (UE) 2021/1119', NULL);

-- Record 2024/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'Regolamento Delegato (UE) 2020/1818 della Commissione del 17 luglio 2020 che integra il regolamento (UE) 2016/1011 del Parlamento europeo e del Consiglio per quanto riguarda le norme minime per gli indici di riferimento UE di transizione climatica e per gli indici di riferimento UE allineati con l’accordo di Parigi', 'Regolamento (UE)', '2020/1818', 'Questo regolamento integra le norme minime per gli indici di riferimento UE di transizione climatica e per gli indici di riferimento UE allineati con l’accordo di Parigi.', 'I regolamenti sono atti giuridici definiti nell’articolo 288 del trattato sul funzionamento dell’Unione europea (TFUE). Hanno portata generale, sono vincolanti in tutti i loro elementi e direttamente applicabili negli Stati membri dell’Unione. I regolamenti rientrano nel diritto derivato dell’Unione. Sono adottati dalle istituzioni dell’Unione europea sulla base dei trattati. Un regolamento ha carattere vincolante in tutti i suoi elementi, ha portata generale ed è rivolto a categorie astratte di persone, non a specifici individui. 
 https://eur-lex.europa.eu/IT/legal-content/summary/european-union-regulations.html', 'Regolamento che integra le norme minime per gli indici di riferimento UE di transizione climatica', 'Regolamento (UE) - 2020/1818', 'Regolamento (UE) 2020/1818', NULL);

-- Record 2025/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (615, 'Strategia Nazionale per lo Sviluppo Sostenibile (SNSvS)', 'Strategia Nazionale (IT)', 'SNSvS', 'In Italia rappresenta il quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali, declinando per il contesto nazionale gli Obiettivi di Sviluppo Sostenibile (SDG) delineati dall''ONU', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia Nazionale italiana per lo Sviluppo Sostenibile', 'SNSvS (2022)', 'SNSvS22- Pianeta III.1', 'Promuovere il presidio e la manutenzione del territorio e rafforzare le capacità di resilienza di comunità e territori anche in riferimento agli impatti dei cambiamenti climatici');

-- Record 2026/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (616, 'SDGs: Goal 14 La vita sott’acqua', 'SDGs', 'Goal 14', 'Conservare e utilizzare in modo durevole gli oceani, i mari e le risorse marine per uno sviluppo sostenibile', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'La vita sott’acqua', 'SDGs - Goal 14', 'SDG 14.1', 'Entro il 2025, prevenire e ridurre in modo significativo ogni forma di inquinamento marino, in particolar modo quello derivante da attività esercitate sulla terraferma, compreso l’inquinamento dei detriti marini e delle sostanze nutritive');

-- Record 2027/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (616, 'SDGs: Goal 6 Acqua pulita e servizi igienico-sanitari', 'SDGs', 'Goal 6', 'Garantire a tutti la disponibilità e la gestione sostenibile dell’acqua e delle strutture igienico-sanitarie', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Acqua pulita e servizi igienico-sanitari', 'SDGs - Goal 6', 'SDG 6.3', 'Migliorare entro il 2030 la qualità dell''acqua eliminando le discariche, riducendo l''inquinamento e il rilascio di prodotti chimici e scorie pericolose, dimezzando la quantità di acque reflue non trattate e aumentando considerevolmente il riciclaggio e il reimpiego sicuro a livello globale.');

-- Record 2028/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (616, 'Costituzione Italiana- Articolo 32: Principio di Diritto alla Salute', 'Costituzione Italiana', 'Articolo 32', 'L''articolo 32 della Costitutione Italiana stabilisce che: "La Repubblica tutela la salute come fondamentale diritto dell’individuo e interesse della collettività, e garantisce cure gratuite agli indigenti. Nessuno può essere obbligato a un determinato trattamento sanitario se non per disposizione di legge. La legge non può in nessun caso violare i limiti imposti dal rispetto della persona umana".', 'La Costituzione Italiana è la legge fondamentale della Repubblica Italiana. Essa stabilisce i principi fondamentali su cui si basa l''ordinamento dello Stato italiano, definendo i diritti e i doveri dei cittadini, l''organizzazione e i poteri delle istituzioni statali (Parlamento, Governo e Magistratura), nonché i principi economici e sociali dello Stato. La Costituzione è la norma suprema, alla quale devono conformarsi tutte le altre leggi e normative del Paese.', 'Principio di Diritto alla Salute', 'Costituzione Italiana - Articolo 32', 'Art. 32 Cost.', 'La Repubblica tutela la salute come fondamentale diritto dell’individuo e interesse della collettività, e garantisce cure gratuite agli indigenti. Nessuno può essere obbligato a un determinato trattamento sanitario se non per disposizione di legge. La legge non può in nessun caso violare i limiti imposti dal rispetto della persona umana.');

-- Record 2029/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (616, 'Decreto Legislativo 152/2006 - Norme in materia ambientale', 'Decreto (IT)', '152/2006', 'Il Decreto Legislativo 3 aprile 2006, n. 152 stabilisce le norme fondamentali per la tutela dell''ambiente in Italia. Questo decreto, noto come "Codice dell''ambiente", regola diversi aspetti relativi alla gestione, alla protezione e alla valorizzazione dell''ambiente.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Norme in materia ambientale', 'Decreto Legislativo - 152/2006', 'D.Lgs. 152/2006', NULL);

-- Record 2030/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (616, 'Decreto Legislativo 6 settembre 2024, n. 125 Attuazione della direttiva 2022/2464/UE del Parlamento europeo e del Consiglio del 14 dicembre 2022, recante modifica del regolamento 537/2014/UE, della direttiva 2004/109/CE, della direttiva 2006/43/CE e della direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Decreto (IT)', '125/2024', 'Il Decreto legislativo 6 settembre 2024, n. 125 attua la Direttiva 2022/2464/UE (nota come Corporate Sustainability Reporting Directive, CSRD). Questo decreto introduce nuovi obblighi di rendicontazione societaria di sostenibilità per le imprese, ampliando notevolmente le informazioni che devono essere incluse nei bilanci aziendali in materia di sostenibilità ambientale, sociale e di governance (ESG).', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Attuazione della direttiva 2022/2464/UE (CSRD)', 'Decreto Legislativo - 125/2024', 'D.Lgs. 125/2024', 'Attuazione della direttiva 2022/2464/UE (CSRD)');

-- Record 2031/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (616, 'Direttiva (UE) 2022/2464 del Parlamento europeo e del Consiglio del 14 dicembre 2022 che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE)', '2022/2464', 'La CSRD o Corporate Sustainability Reporting Directive (Direttiva 2022/2464) è la direttiva europea che mira a migliorare la trasparenza e la qualità delle informazioni sulla sostenibilità fornite dalle aziende. La CSRD sostituisce e amplia la Non-Financial Reporting Directive (NFRD), aumentando le esigenze di rendicontazione per le imprese riguardo alle loro attività sostenibili. I punti chiavi della CSRD sono: -L''ampiezza del campo di applicazione rispetto alla NFRD rispetto alle aziende che devono rendicontare, - le informazioni da rendicontare in termini ESG e di sostenibilità; - lo standard di rendicontazione (gli ESRS); - L''audit e certificazione; e la digitalizzazione delle informazioni. 
 
 L''obiettivo principale della CSRD è migliorare la trasparenza e responsabilizzare le aziende verso pratiche più sostenibili, aiutando investitori, consumatori, e altre parti interessate a prendere decisioni informate basate su dati di sostenibilità accurati e completi.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE) - 2022/2464', 'CSRD', 'Rendicontazione societaria di sostenibilità (CSRD)');

-- Record 2032/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (616, 'ESRS E2- Inquinamento', 'ESRS', 'E2', 'Questo Standard stabilisce i requisiti di Informativa GRI  relativi ai seguenti aspetti della sostenibilità: inquinamento dell''aria, dell''acqua, del suolo, sostanze preoccupanti, comprese le sostanze estremamente preoccupanti.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Inquinamento', 'ESRS - E2', 'ESRS (E2) E2-4 - 28 a  AR 21-22', 'Inquinamento dell''aria, dell''acqua e del suolo [dimensioni multiple: a livello di sito o per tipo di fonte, per settore o per area geografica]');

-- Record 2033/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (616, 'EU - Strategia a lungo termine per la neutralità climatica al 2050', 'Strategia (UE)', 'Neutralità climatica al 2050', 'Strategia con l''obiettivo di raggiungere un''Unione Europea climaticamente neutrale entro il 2050, in linea con gli obiettivi dell''Accordo di Parigi e alla luce delle più recenti conoscenze scientifiche disponibili e della necessità di intensificare l''azione climatica globale.', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Strategia a lungo termine per la neutralità climatica al 2050', 'Strategia (UE) - Neutralità climatica al 2050', 'Strategia (UE) Neutralità climatica al 2050', 'Strategia a lungo termine per la neutralità climatica al 2050');

-- Record 2034/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (616, 'GRI 303- Acqua ed effluenti (2018)', 'GRI', '303', 'Questo Standard tratta il tema dell''acqua e degli effluenti. Contiene informative per le organizzazioni che devono rendicontare informazioni sui loro impatti correlati all''acqua e la relativa gestione.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Acqua ed effluenti (2018)', 'GRI - 303 (2018)', 'GRI 303-1 (2018)', 'Interazioni con l''acqua come risorsa condivisa');

