INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (617, 'Global Compact delle Nazioni Unite (UNGC): Principio 7- Ambiente', 'UNGC', 'Principio 7', 'Le imprese dovrebbero sostenere un approccio precauzionale alle sfide ambientali', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Ambiente', 'UNGC - Principio 7', 'UNGC P.7', 'Le imprese dovrebbero sostenere un approccio precauzionale alle sfide ambientali');

-- Record 2050/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (617, 'Strategia Nazionale per lo Sviluppo Sostenibile (SNSvS)', 'Strategia Nazionale (IT)', 'SNSvS', 'In Italia rappresenta il quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali, declinando per il contesto nazionale gli Obiettivi di Sviluppo Sostenibile (SDG) delineati dall''ONU', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia Nazionale italiana per lo Sviluppo Sostenibile', 'SNSvS (2022)', 'SNSvS22- Pianeta II.1', 'Mantenere la vitalità dei mari e prevenire gli impatti sull’ambiente marino e costiero');

-- Record 2051/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (617, 'ESRS E4- Biodiversità ed ecosistemi', 'ESRS', 'E4', 'Questo Standard stabilisce i requisiti di Informativa relativi alla relazione dell''impresa con gli habitat terrestri, d''acqua dolce e marini, gli ecosistemi e le popolazioni di specie faunistiche e floristiche correlate, inclusa la diversità all''interno delle specie, tra le specie e degli ecosistemi, e la loro interrelazione con i popoli indigeni e altre comunità colpite.
 
 I termini "biodiversità" e "diversità biologica" si riferiscono alla variabilità tra gli organismi viventi di tutte le provenienze, compresi, tra l''altro, gli ecosistemi terrestri, d''acqua dolce, marini e altri ecosistemi acquatici e i complessi ecologici di cui fanno parte.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Biodiversità ed ecosistemi', 'ESRS - E4', 'ESRS (E4) E4-1 AR 1 d', 'Dichiarazione del contributo ai fattori di impatto e delle possibili azioni di mitigazione secondo la gerarchia di mitigazione, incluse le principali dipendenze strutturali e gli asset e le risorse vincolati associati ai cambiamenti della biodiversità e degli ecosistemi.');

-- Record 2052/2644
