INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (619, 'Strategia dell''UE sulla biodiversità per il 2030', 'Strategia (UE)', 'Biodiversità (2030)', 'La Strategia dell''UE per la biodiversità per il 2030 presenta un pacchetto completo di impegni e azioni per mettere la biodiversità dell''Europa sulla via della ripresa entro il 2030, in linea con l''Agenda 2030 per lo sviluppo sostenibile e con gli obiettivi dell''Accordo di Parigi sui cambiamenti climatici.
 
 La Strategia mira a affrontare i cinque principali fattori di perdita della biodiversità e a mettere in atto un quadro di governance rafforzato, oltre a colmare eventuali lacune politiche, consolidando allo stesso tempo gli sforzi esistenti e garantendo la piena attuazione della legislazione UE vigente.', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Strategia dell''UE sulla biodiversità per il 2030', 'Strategia (UE) - Biodiversità (2030)', 'Strategia (UE) Biodiversità (2030)', 'Strategia dell''UE sulla biodiversità per il 2030');

-- Record 2088/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (619, 'Strategia Nazionale per la Biodiversità al 2030', 'Strategia Nazionale (IT)', 'Biodiversità (2030)', 'La Strategia Nazionale per la Biodiversità al 2030 in Italia è un piano d''azione elaborato per proteggere, conservare e ripristinare la biodiversità del paese entro l''anno 2030. Prevede l’identificazione di due obiettivi strategici declinati in otto Ambiti di intervento (Aree Protette; Specie, Habitat ed Ecosistemi; Cibo e Sistemi Agricoli, Zootecnia; Foreste; Verde Urbano; Acque Interne; Mare; Suolo)', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia Nazionale per la Biodiversità al 2030', 'Strategia Nazionale - Biodiversità (2030)', 'Strategia (IT)  Biodiversità 2030', 'Strategia Nazionale per la Biodiversità al 2030');

-- Record 2089/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (619, 'Strategia Nazionale per lo Sviluppo Sostenibile (SNSvS)', 'Strategia Nazionale (IT)', 'SNSvS', 'In Italia rappresenta il quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali, declinando per il contesto nazionale gli Obiettivi di Sviluppo Sostenibile (SDG) delineati dall''ONU', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia Nazionale italiana per lo Sviluppo Sostenibile', 'SNSvS (2022)', 'SNSvS22- Pianeta III.3', 'Garantire il ripristino e la deframmentazione degli ecosistemi e favorire le connessioni ecologiche urbano-rurali');

