INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (626, 'ESRS E1- Cambiamento climatico', 'ESRS', 'E1', 'Questo Standard copre i requisiti di Informativa GRI  relativi ai seguenti aspetti della sostenibilità: "Mitigazione del cambiamento climatico" e "Adattamento al cambiamento climatico". Copre anche le questioni energetiche, nella misura in cui sono rilevanti per il cambiamento climatico.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Cambiamento climatico', 'ESRS - E1', 'ESRS (E1) E1-3 28', 'Azioni e risorse relative alla mitigazione e all''adattamento ai cambiamenti climatici');

-- Record 2175/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (626, 'EU - Strategia a lungo termine per la neutralità climatica al 2050', 'Strategia (UE)', 'Neutralità climatica al 2050', 'Strategia con l''obiettivo di raggiungere un''Unione Europea climaticamente neutrale entro il 2050, in linea con gli obiettivi dell''Accordo di Parigi e alla luce delle più recenti conoscenze scientifiche disponibili e della necessità di intensificare l''azione climatica globale.', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Strategia a lungo termine per la neutralità climatica al 2050', 'Strategia (UE) - Neutralità climatica al 2050', 'Strategia (UE) Neutralità climatica al 2050', 'Strategia a lungo termine per la neutralità climatica al 2050');

