INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (626, 'GRI 201- Performance economica (2016)', 'GRI', '201', 'Lo Standard 201 tratta il tema della performance economica, che include il valore economico generato e distribuito (EVG&D) da un’organizzazione, gli obblighi relative al piano di benefit definiti, l’assistenza finanziaria che riceve da qualsiasi governo e le implicazioni finanziarie del cambiamento climatico.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Performance economica (2016)', 'GRI - 201 (2016)', 'GRI 201-2 (2016)', 'Implicazioni finanziarie e altri rischi e opportunità dovuti al cambiamento climatico');

-- Record 2178/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (626, 'IFRS S2 - Climate-related Disclosures', 'IFRS', 'S2', 'L''obiettivo dello IFRS S2 Climate-related Disclosures è richiedere alle entità di divulgare informazioni sui rischi e le opportunità legati al clima, utili agli utenti principali dei report finanziari per prendere decisioni sulla fornitura di risorse.', 'L''IFRS S2 Climate-related Disclosures richiede che un''entità fornisca informazioni sui propri rischi e opportunità legati al clima affinché tali informazioni siano utili ai principali utilizzatori dei bilanci finanziari generali, consentendo loro di prendere decisioni informate sulla fornitura di risorse all''entità.', 'Divulgazioni relative al clima', 'IFRS- S2', 'IFRS S2.10', 'Rischi e opportunità legati al clima.');

-- Record 2179/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (626, 'Piano Nazionale Adattamento Cambiamenti Climatici (PNACC)', 'Piano Nazionale (IT)', 'Adattamento Cambiamento Climatico', 'La finalità del Piano è contenere la vulnerabilità dei sistemi naturali, sociali ed economici agli impatti dei cambiamenti climatici e aumentarne la resilienza. Rappresenta lo strumento di attuazione della Strategia Nazionale di adattamento ai cambiamenti climatici del 2015 per la pianificazione nazionale a supporto delle istituzioni che saranno chiamate a sviluppare sulla propria scala di governo i contenuti del piano, tenendo conto delle specificità dei diversi contesti.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Cambiamento Climatico (PNACC)', 'Piano Nazionale (IT) Adattamento Cambiamento Climatico', 'PNACC', 'Piano Nazionale Adattamento Cambiamenti Climatici');

