INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (626, 'Raccomandazioni della Task Force sulle Informazioni Finanziarie relative al Clima (TCFD) - Rischi, opportunità e impatti finanziari legati al clima', 'TCFD', 'Rischi, opportunità e impatti finanziari legati al clima', 'Rischi, Opportunità e Impatti Finanziari legati al Clima identifica la crescente richiesta di informazioni finanziarie utili e orientate al clima da parte di investitori, finanziatori, assicuratori e altri stakeholder. La Task Force sottolinea l''importanza di un quadro standardizzato per allineare le diverse normative e favorire la coerenza nelle informazioni a livello internazionale.', 'La Task Force sulle Informazioni Finanziarie relative al Clima (Task Force on Climate-related Financial Disclosures, TCFD)  ha il compito di promuovere la trasparenza nelle informazioni finanziarie legate al clima, rendendo i dati più comparabili, coerenti e utili per investitori, creditori, assicuratori e altre parti interessate.', 'Clima', 'TCFD - Rischi, opportunità e impatti finanziari legati al clima', 'TCFD - Clima', 'Rischi, opportunità e impatti finanziari legati al clima');

