INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (633, 'Accordo relativo al trasporto internazionale di merci pericolose su strada (ADR)', 'Trattato internazionale', 'ADR', 'L''ADR (Accordo sul trasporto internazionale di merci pericolose su strada) è un trattato internazionale che regola il trasporto su strada di merci pericolose in Europa e in altri Paesi partecipanti. È stato adottato dalla Commissione economica per l''Europa delle Nazioni Unite (UNECE) nel 1957 e viene aggiornato regolarmente.', 'L''accordo internazionale può essere considerato come un contratto stipulato tra due o più Stati, diretto a regolare una determinata sfera di rapporti tra i contraenti. La sua formazione è regolata da una serie di norme consuetudinarie e convenzionali che rientrano nel diritto pubblico internazionale.', 'Accordo relativo al trasporto internazionale di merci pericolose su strada', 'Trattato internazionale - Accordo relativo al trasporto internazionale di merci pericolose su strada (ADR)', 'ADR (1957)', 'Trasporto internazionale di merci pericolose su strada');

-- Record 2250/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (633, 'Direttiva 2008/68/CE del Parlamento europeo e del Consiglio
del 24 settembre 2008
relativa al trasporto interno di merci pericolose', 'Direttiva (UE)', '2008/68', 'La Direttiva 2008/68/CE armonizza le norme sul trasporto interno di merci pericolose via strada (ADR), ferrovia (RID) e vie navigabili interne (ADN) negli Stati membri dell''UE, garantendo sicurezza e uniformità nelle regolamentazioni. Essa impone agli Stati di adottare e applicare le disposizioni tecniche internazionali per prevenire rischi per persone, beni e ambiente.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva relativa al trasporto interno di merci pericolose', 'Direttiva (UE) - 2008/68', 'Direttiva (UE) - 2008/68', 'Trasporto internazionale di merci pericolose su strada');

-- Record 2251/2644
INSERT INTO esg_impact.form_legal_bases
