INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (643, 'Piano Nazionale Adattamento Cambiamenti Climatici (PNACC)', 'Piano Nazionale (IT)', 'Adattamento Cambiamento Climatico', 'La finalità del Piano è contenere la vulnerabilità dei sistemi naturali, sociali ed economici agli impatti dei cambiamenti climatici e aumentarne la resilienza. Rappresenta lo strumento di attuazione della Strategia Nazionale di adattamento ai cambiamenti climatici del 2015 per la pianificazione nazionale a supporto delle istituzioni che saranno chiamate a sviluppare sulla propria scala di governo i contenuti del piano, tenendo conto delle specificità dei diversi contesti.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Cambiamento Climatico (PNACC)', 'Piano Nazionale (IT) Adattamento Cambiamento Climatico', 'PNACC', 'Piano Nazionale Adattamento Cambiamenti Climatici');

-- Record 2321/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (643, 'Piano Nazionale di contabilizzazione forestale dell’Italia (2021-2025)', 'Piano Nazionale (IT)', 'Contabilizzazione forestale dell’Italia', 'Il Piano Nazionale di Contabilizzazione Forestale dell''Italia (PNCF) è un documento strategico che descrive come il paese intende monitorare, rendicontare e gestire il carbonio forestale, in conformità con gli obblighi internazionali e le politiche nazionali di mitigazione dei cambiamenti climatici. Questo piano è una parte essenziale delle politiche ambientali e climatiche italiane, poiché le foreste giocano un ruolo cruciale nel sequestro del carbonio e nella riduzione delle emissioni di gas serra.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Piano di contabilizzazione forestale dell’Italia (2021-2025)', 'Piano Nazionale (IT) - Contabilizzazione forestale dell’Italia', 'PNCFI (NFAP)', 'Piano Nazionale di contabilizzazione forestale dell’Italia');

-- Record 2322/2644
