INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (607, 'Direttiva (UE) del Parlamento Europeo e del Consiglio del 20 dicembre 1994 sugli imballaggi e i rifiuti di imballaggio', 'Direttiva (UE)', '94/62/CE', 'Questa direttiva europea stabilisce le regole per la gestione degli imballaggi e dei rifiuti di imballaggio, con l''obiettivo di ridurre l''impatto ambientale attraverso il riciclo e il riutilizzo. Impone agli Stati membri di adottare misure per ridurre la quantità di packaging immesso sul mercato e promuovere il recupero.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva sugli imballaggi e i rifiuti di imballaggio', 'Direttiva (UE) - 94/62/CE', 'Direttiva (UE) 94/62/CE', 'Direttiva sugli imballaggi e i rifiuti di imballaggio');

