INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (663, 'ESRS G1- Condotta aziendale', 'ESRS', 'G1', 'L''obiettivo di questo Standard è specificare i requisiti di divulgazione che consentiranno agli utenti delle dichiarazioni di sostenibilità dell''azienda di comprendere la strategia e l''approccio dell''azienda, i processi e le procedure, nonché le sue performance riguardo alla condotta aziendale.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Condotta aziendale', 'ESRS - G1', 'ESRS (G1) G1-3 - 18a', 'Informazioni sulle procedure in atto per prevenire, rilevare e affrontare accuse o incidenti di corruzione o concussione');

-- Record 2397/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (663, 'ISO 37001: Sistemi di gestione per la prevenzione della corruzione', 'ISO', '37001', 'L''ISO 37001 è uno standard internazionale che fornisce linee guida e requisiti per l''implementazione di un sistema di gestione antisovvenzione (Anti-Bribery Management System, ABMS). L''obiettivo principale è quello di aiutare le organizzazioni a stabilire, implementare, mantenere, aggiornare e migliorare un sistema di gestione efficace per prevenire la corruzione e aderire ai requisiti legali e regolamentari applicabili.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Sistemi di gestione per la prevenzione della corruzione', 'ISO - 37001', 'ISO 37001', 'Sistemi di gestione per la prevenzione della corruzione');

